        self.action = action


class SmartHomeController:
    def turnOn(self, d: SmartDevice):
        if d.getType() == DeviceType.LIGHT: print("Light", d.getId(), "ON")
        elif d.getType() == DeviceType.THERMOSTAT: print("Thermostat", d.getId(), "heating")
        elif d.getType() == DeviceType.DOOR_LOCK: print("Lock", d.getId(), "stays locked")
        elif d.getType() == DeviceType.CAMERA: print("Camera", d.getId(), "recording")
