        elif d.getType() == DeviceType.THERMOSTAT: print("Thermostat", d.getId(), "idle")
        elif d.getType() == DeviceType.DOOR_LOCK: print("Lock", d.getId(), "remains locked")
        elif d.getType() == DeviceType.CAMERA: print("Camera", d.getId(), "standby")
        elif d.getType() == DeviceType.SPRINKLER: print("Sprinkler", d.getId(), "stopped")

    def setMode(self, d: SmartDevice, m: Mode):
        d.setMode(m)
        print(d.getType().name.capitalize(), d.getId(), "mode", m.name)

    def energyUsageKwh(self, d: SmartDevice, minutes: int) -> float:
        if d.getType() == DeviceType.LIGHT: return 0.012 * minutes
        elif d.getType() == DeviceType.THERMOSTAT: return 0.08 * minutes
        elif d.getType() == DeviceType.DOOR_LOCK: return 0.0002 * minutes
        elif d.getType() == DeviceType.CAMERA: return 0.01 * minutes
        elif d.getType() == DeviceType.SPRINKLER: return 0.03 * minutes
        return 0.0

