            DeviceType.DOOR_LOCK: "🔒",
            DeviceType.CAMERA: "📷",
            DeviceType.SPRINKLER: "💧"
        }.get(d.getType(), "❓")

    def firmwareUpdateSteps(self, d: SmartDevice) -> List[str]:
        if d.getType() == DeviceType.LIGHT: return ["Ping", "Push FW", "Reboot"]
        elif d.getType() == DeviceType.THERMOSTAT: return ["Ping", "Backup Profile", "Push FW", "Recalibrate"]
        elif d.getType() == DeviceType.DOOR_LOCK: return ["Ping", "Secure Channel", "Push FW", "Reauth"]
        elif d.getType() == DeviceType.CAMERA: return ["Ping", "Push FW", "Restart Stream"]
        elif d.getType() == DeviceType.SPRINKLER: return ["Ping", "Push FW", "Valve Test"]
        return []


