class SecurityPolicy:
    def actionAllowed(self, d: SmartDevice, action: str, userHome: bool) -> bool:
        if d.getType() == DeviceType.DOOR_LOCK:
            return action == "unlock" and userHome or action != "unlock"
        elif d.getType() == DeviceType.CAMERA:
            return True
        elif d.getType() in [DeviceType.THERMOSTAT, DeviceType.LIGHT]:
            return True
