        elif d.getType() == DeviceType.CAMERA:
            if phrase.lower() == "start recording": self._ctl.turnOn(d)
        elif d.getType() == DeviceType.SPRINKLER:
            if phrase.lower() == "water garden": self._ctl.turnOn(d)


class SceneManager:
    def __init__(self): self._ctl = SmartHomeController()
    def applyScene(self, name: str, devices: List[SmartDevice]):
        for d in devices:
            if name == "Evening":
                if d.getType() == DeviceType.LIGHT: self._ctl.turnOn(d); self._ctl.setMode(d, Mode.COMFORT)
                elif d.getType() == DeviceType.THERMOSTAT: self._ctl.setMode(d, Mode.COMFORT)
