    def notifyOwner(self, d: SmartDevice, event: str): print("Notify: Camera " + d.getId() + " -> " + event)
    def health(self, d: SmartDevice) -> str: return "OK" if d.isOnline() else "Stream Error"
    def maintenanceCostYearly(self) -> float: return 18.0
    def actionAllowed(self, action: str, userHome: bool) -> bool: return True
    def handleVoice(self, phrase: str, ctl, d: SmartDevice):
        if phrase.lower() == "start recording": ctl.turnOn(d)
    def applyScene(self, scene: str, ctl, d: SmartDevice):
        if scene == "Evening" or scene == "Away": ctl.turnOn(d)

class SprinklerOps(DeviceOps):
    def turnOn(self, d: SmartDevice): print("Sprinkler " + d.getId() + " watering")
    def turnOff(self, d: SmartDevice): print("Sprinkler " + d.getId() + " stopped")
    def setMode(self, d: SmartDevice, m: Mode): d.setMode(m); print("Sprinkler " + d.getId() + " mode " + str(m.name))
    def energyUsageKwh(self, minutes: int) -> float: return 0.03 * minutes
    def icon(self) -> str: return "💧"
    def firmwareUpdateSteps(self) -> List[str]: return ["Ping", "Push FW", "Valve Test"]
    def notifyOwner(self, d: SmartDevice, event: str): print("Notify: Sprinkler " + d.getId() + " -> " + event)
    def health(self, d: SmartDevice) -> str: return "OK" if d.isOnline() else "Valve Error"
    def maintenanceCostYearly(self) -> float: return 15.0
    def actionAllowed(self, action: str, userHome: bool) -> bool: return (action != "on") or (not userHome)
    def handleVoice(self, phrase: str, ctl, d: SmartDevice):
        if phrase.lower() == "water garden": ctl.turnOn(d)
    def applyScene(self, scene: str, ctl, d: SmartDevice):
        if scene == "Evening" or scene == "Away": ctl.turnOff(d)

class SmartHomeController:
    def turnOn(self, d: SmartDevice): DeviceOpsRegistry.get(d.getType()).turnOn(d)
    def turnOff(self, d: SmartDevice): DeviceOpsRegistry.get(d.getType()).turnOff(d)
    def setMode(self, d: SmartDevice, m: Mode): DeviceOpsRegistry.get(d.getType()).setMode(d, m)
    def energyUsageKwh(self, d: SmartDevice, minutes: int) -> float: return DeviceOpsRegistry.get(d.getType()).energyUsageKwh(minutes)
    def deviceIcon(self, d: SmartDevice) -> str: return DeviceOpsRegistry.get(d.getType()).icon()
    def firmwareUpdateSteps(self, d: SmartDevice) -> List[str]: return DeviceOpsRegistry.get(d.getType()).firmwareUpdateSteps()

class NotificationService:
    def notifyOwner(self, d: SmartDevice, event: str): DeviceOpsRegistry.get(d.getType()).notifyOwner(d, event)

class DiagnosticsService:
    def health(self, d: SmartDevice) -> str: return DeviceOpsRegistry.get(d.getType()).health(d)
    def maintenanceCostYearly(self, d: SmartDevice) -> float: return DeviceOpsRegistry.get(d.getType()).maintenanceCostYearly()

class SecurityPolicy:
    def actionAllowed(self, d: SmartDevice, action: str, userHome: bool) -> bool:
        return DeviceOpsRegistry.get(d.getType()).actionAllowed(action, userHome)

class VoiceCommandProcessor:
    def __init__(self):
        self._ctl = SmartHomeController()
    def handle(self, phrase: str, d: SmartDevice):
        DeviceOpsRegistry.get(d.getType()).handleVoice(phrase, self._ctl, d)

class SceneManager:
    def __init__(self):
        self._ctl = SmartHomeController()
    def applyScene(self, name: str, devices: List[SmartDevice]):
        for d in devices:
            DeviceOpsRegistry.get(d.getType()).applyScene(name, self._ctl, d)

class EnergyEstimator:
    def __init__(self):
        self._ctl = SmartHomeController()
    def estimateRoom(self, room: Room, minutes: int) -> float:
        sum_ = 0.0
        for d in room.getDevices():
            sum_ += self._ctl.energyUsageKwh(d, minutes)
        return sum_
