public abstract class Bird {
    protected String species;
    protected double wingSpan;

    public Bird(String species, double wingSpan) {
        this.species = species;
        this.wingSpan = wingSpan;
    }

    public abstract void fly();
    public abstract double getFlightSpeed();

    public String getSpecies() {
        return species;
    }

    public double getWingSpan() {
        return wingSpan;
    }
}

public class Eagle extends Bird {
    public Eagle(double wingSpan) {
        super("Eagle", wingSpan);
    }

    @Override
    public void fly() {
        System.out.println("Eagle soaring at high altitude with " + wingSpan + "m wingspan");
    }

    @Override
    public double getFlightSpeed() {
        return 80.0 + (wingSpan * 10);
    }
}

public class Hummingbird extends Bird {
    public Hummingbird(double wingSpan) {
        super("Hummingbird", wingSpan);
    }

    @Override
    public void fly() {
        System.out.println("Hummingbird hovering with rapid wing beats");
    }

    @Override
    public double getFlightSpeed() {
        return 50.0;
    }
}

public class Ostrich extends Bird {
    private double runningSpeed;

    public Ostrich(double wingSpan, double runningSpeed) {
        super("Ostrich", wingSpan);
        this.runningSpeed = runningSpeed;
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Ostriches are flightless birds!");
    }

    @Override
    public double getFlightSpeed() {
        return 0.0;
    }

    public void run() {
        System.out.println("Ostrich running at " + runningSpeed + " km/h");
    }
}

public class FlightSimulator {
    public void simulateFlight(Bird bird, double distance) {
        System.out.println("Starting flight simulation for " + bird.getSpecies());
        bird.fly();
        double speed = bird.getFlightSpeed();
        double time = distance / speed;
        System.out.println("Flight time: " + time + " hours for " + distance + " km");
    }

    public void compareFlightCapabilities(Bird[] birds, double testDistance) {
        for (Bird bird : birds) {
            simulateFlight(bird, testDistance);
            System.out.println("---");
        }
    }
}

