from abc import ABC, abstractmethod

class Bird(ABC):
    def __init__(self, species, wing_span):
        self.species = species
        self.wing_span = wing_span

    @abstractmethod
    def fly(self):
        pass

    @abstractmethod
    def get_flight_speed(self):
        pass

    def get_species(self):
        return self.species

    def get_wing_span(self):
        return self.wing_span

class Eagle(Bird):
    def __init__(self, wing_span):
        super().__init__("Eagle", wing_span)

    def fly(self):
        print(f"Eagle soaring at high altitude with {self.wing_span}m wingspan")

    def get_flight_speed(self):
        return 80.0 + (self.wing_span * 10)

class Hummingbird(Bird):
    def __init__(self, wing_span):
        super().__init__("Hummingbird", wing_span)

    def fly(self):
        print("Hummingbird hovering with rapid wing beats")

    def get_flight_speed(self):
        return 50.0

class Ostrich(Bird):
    def __init__(self, wing_span, running_speed):
        super().__init__("Ostrich", wing_span)
        self.running_speed = running_speed

    def fly(self):
        raise NotImplementedError("Ostriches are flightless birds!")

    def get_flight_speed(self):
        return 0.0

    def run(self):
        print(f"Ostrich running at {self.running_speed} km/h")

class FlightSimulator:
    def simulate_flight(self, bird, distance):
        print(f"Starting flight simulation for {bird.get_species()}")
        bird.fly()
        speed = bird.get_flight_speed()
        time = distance / speed if speed > 0 else 0
        print(f"Flight time: {time} hours for {distance} km")

    def compare_flight_capabilities(self, birds, test_distance):
        for bird in birds:
            self.simulate_flight(bird, test_distance)
            print("---")

