        fuel_service = self._get_fuel_service(fuel_type)
        if not fuel_service:
            print(f"No fuel service available for {fuel_type}")
            return

        print(f"Starting fleet refueling with {fuel_type}")
        for vehicle in fleet:
            if fuel_service.can_service(vehicle.vehicle_type):
