            writer.print(data);
        }
    }
}

public class DatabaseConnectionService implements DatabaseService {
    private Map<String, Object> configuration;

    public DatabaseConnectionService(Map<String, Object> config) {
        this.configuration = config;
    }

    @Override
    public Connection getConnection() throws Exception {
        String url = (String) configuration.get("db.url");
        String user = (String) configuration.get("db.user");
        String password = (String) configuration.get("db.password");

        if (url == null) throw new IllegalStateException("Database URL not configured");

        Thread.sleep(100);
        return null;
    }

    @Override
    public void executeQuery(Connection connection, String query) throws Exception {
        try (PreparedStatement statement = connection.prepareStatement(query)) {
            statement.execute();
        }
    }

    @Override
