        pass

class InMemoryProcessorRegistry(ProcessorRegistry):
    def __init__(self):
        self._processors: List[DataProcessor] = []

    def find_processor(self, format_type: str) -> Optional[DataProcessor]:
        for processor in self._processors:
            if processor.can_process(format_type):
                return processor
        return None

    def add_processor(self, processor: DataProcessor) -> None:
        self._processors.append(processor)

    def get_all_processors(self) -> List[DataProcessor]:
        return self._processors.copy()

class ProcessingOrchestrator:
    def __init__(self, registry: ProcessorRegistry):
        self._registry = registry
        self._processing_semaphore = asyncio.Semaphore(4)

    async def process_all_data(self, inputs: List[DataInput], options: ProcessingOptions) -> List[ProcessingResult]:
        results = []

        for input_data in inputs:
            processor = self._registry.find_processor(input_data.format_type)
            if not processor:
                results.append(ProcessingResult(
                    records_processed=0,
                    output_location=None,
                    status_message=f"No processor found for format: {input_data.format_type}",
                    processing_time=0.0,
                    errors=[f"Unsupported format: {input_data.format_type}"]
                ))
                continue

            try:
                async with self._processing_semaphore:
                    result = await asyncio.wait_for(
                        processor.process_data(input_data, options),
                        timeout=options.timeout_seconds
                    )
                    results.append(result)
            except Exception as e:
                results.append(ProcessingResult(
                    records_processed=0,
                    output_location=None,
                    status_message=f"ORCHESTRATOR_ERROR: {str(e)}",
                    processing_time=0.0,
                    errors=[str(e)]
                ))

        return results

    def shutdown(self) -> None:
        for processor in self._registry.get_all_processors():
            processor.cleanup()

