        self.content = content
        self.format = format_type
        self.encrypted = False

    @abstractmethod
    def save(self):
        pass

    @abstractmethod
    def print_doc(self):
        pass

    @abstractmethod
    def export(self, target_format):
        pass

    @abstractmethod
    def compress(self):
        pass

