        this.encrypted = false;
        this.timeProvider = timeProvider;
        this.createdAt = timeProvider.Now;
        this.lastModified = timeProvider.Now;
        this.metadata = new Dictionary<string, string>();
        this.revisionHistory = new List<string>();
        this.permissions = permissions;
        this.revisionHistory.Add($"Document created at {createdAt}");
    }

    public abstract void Save();
    public abstract void Print();
    public abstract void Export(string targetFormat);
    public abstract void Compress();
    public abstract void Backup(string location);
    public abstract DocumentStatistics GetStatistics();
    public abstract void AddWatermark(string watermarkText);
    public abstract void DigitallySign(string certificate);

    protected void UpdateLastModified()
    {
        lastModified = timeProvider.Now;
        revisionHistory.Add($"Document modified at {lastModified}");
    }

    public string GetContent() => content;
    public string GetFormat() => format;
    public bool IsEncrypted() => encrypted;
    public DocumentPermissions GetPermissions() => permissions;
}

public class EnterpriseWordDocument : Document
{
    private string author;
    private string department;
    private List<string> collaborators;
    private ReaderWriterLockSlim documentLock;
    private bool isBeingEdited;
    private readonly IDocumentStorage storageService;
    private readonly IPrintService printService;
    private readonly IExportService exportService;
    private readonly ICompressionService compressionService;
    private readonly IStatisticsService statisticsService;
    private readonly IWatermarkService watermarkService;
    private readonly IDigitalSignatureService signatureService;

    public EnterpriseWordDocument(string content, string author, string department,
        IDocumentStorage storageService, IPrintService printService, IExportService exportService,
        ICompressionService compressionService, IStatisticsService statisticsService,
        IWatermarkService watermarkService, IDigitalSignatureService signatureService,
        ITimeProvider timeProvider) 
        : base(content, "DOCX", new DocumentPermissions(true, true, true, true, true), timeProvider)
    {
        this.author = author;
        this.department = department;
        this.collaborators = new List<string>();
        this.documentLock = new ReaderWriterLockSlim();
        this.isBeingEdited = false;
        this.storageService = storageService;
        this.printService = printService;
        this.exportService = exportService;
        this.compressionService = compressionService;
        this.statisticsService = statisticsService;
        this.watermarkService = watermarkService;
        this.signatureService = signatureService;
        this.metadata["author"] = author;
        this.metadata["department"] = department;
    }

    public override void Save()
    {
        documentLock.EnterWriteLock();
        try
        {
            if (!permissions.CanWrite)
            {
                throw new DocumentException("No write permission for this document");
            }
            var metadataString = $"Author: {author}, Department: {department}";
            storageService.Save(content, format, metadataString);
            UpdateLastModified();
            isBeingEdited = false;
        }
        finally
        {
            documentLock.ExitWriteLock();
        }
    }

    public override void Print()
    {
        if (!permissions.CanPrint)
        {
            throw new DocumentException("No print permission for this document");
        }
        printService.Print(content, format);
    }

    public override void Export(string targetFormat)
    {
        if (!permissions.CanExport)
        {
            throw new DocumentException("No export permission for this document");
        }
        exportService.Export(content, format, targetFormat);
    }

    public override void Compress()
    {
        content = compressionService.Compress(content);
        UpdateLastModified();
    }

    public override void Backup(string location)
    {
        if (!permissions.CanBackup)
        {
            throw new DocumentException("No backup permission for this document");
        }
        storageService.Backup(content, location);
    }

    public override DocumentStatistics GetStatistics()
    {
        return statisticsService.CalculateStatistics(content, format);
    }

    public override void AddWatermark(string watermarkText)
    {
        watermarkService.AddWatermark(content, watermarkText);
        UpdateLastModified();
    }

    public override void DigitallySign(string certificate)
    {
        signatureService.DigitallySign(content, certificate);
        encrypted = true;
        UpdateLastModified();
    }

    public void AddCollaborator(string collaborator)
    {
        collaborators.Add(collaborator);
        UpdateLastModified();
    }

    public void StartEditing()
    {
        documentLock.EnterWriteLock();
        try
        {
            isBeingEdited = true;
            Console.WriteLine($"Document is now being edited by {author}");
        }
        finally
        {
            documentLock.ExitWriteLock();
        }
    }
}

public class LegacyDocument : Document
{
    private string originalSystem;
    private bool conversionRequired;
    private List<string> conversionErrors;
    private readonly IStatisticsService statisticsService;

    public LegacyDocument(string content, string originalSystem, IStatisticsService statisticsService, ITimeProvider timeProvider) 
        : base(content, "LEGACY", new DocumentPermissions(true, false, false, false, false), timeProvider)
    {
        this.originalSystem = originalSystem;
        this.conversionRequired = true;
        this.conversionErrors = new List<string>();
        this.statisticsService = statisticsService;
        this.metadata["originalSystem"] = originalSystem;
        this.metadata["migrationStatus"] = "pending";
    }

    public override void Save()
    {
        throw new DocumentException("Legacy documents are read-only and cannot be saved in modern format");
    }

    public override void Print()
    {
        throw new DocumentException("Legacy documents cannot be printed without conversion");
    }

    public override void Export(string targetFormat)
    {
        throw new DocumentException("Legacy documents cannot be exported without manual conversion");
    }

    public override void Compress()
    {
        throw new DocumentException("Legacy documents use proprietary compression that cannot be modified");
    }

    public override void Backup(string location)
    {
        throw new DocumentException("Legacy documents require specialized backup procedures");
    }

    public override DocumentStatistics GetStatistics()
    {
        return statisticsService.CalculateStatistics(content, format);
    }

    public override void AddWatermark(string watermarkText)
    {
        throw new DocumentException("Cannot add watermark to legacy document format");
    }

    public override void DigitallySign(string certificate)
    {
        throw new DocumentException("Digital signatures not supported for legacy document format");
    }

    public void AttemptConversion()
    {
        Console.WriteLine($"Attempting to convert legacy document from {originalSystem}");
        conversionErrors.Add("Formatting may be lost during conversion");
        conversionErrors.Add("Some features not supported in modern format");
    }

    public List<string> GetConversionErrors() => new List<string>(conversionErrors);
    public bool IsConversionRequired() => conversionRequired;
}
