        self.routing_number = routing_number

    def process_payment(self, amount):
        if amount < 100:
            raise ValueError("Bank transfers must be at least $100")
        if not self.validate_payment(amount):
            raise RuntimeError("Invalid bank transfer")
        print(f"Processing bank transfer of ${amount}")
        # Bank transfers are processed immediately

    def validate_payment(self, amount):
        return self.account_number and self.routing_number and amount >= 100

    def get_payment_method(self):
