import java.math.BigDecimal;
import java.time.LocalDateTime;
import java.util.*;
import java.util.concurrent.*;
import java.util.logging.Logger;

public abstract class FinancialInstrument {
    protected String instrumentId;
    protected BigDecimal faceValue;
    protected LocalDateTime creationTime;
    protected static final Logger logger = Logger.getLogger(FinancialInstrument.class.getName());

    public FinancialInstrument(String instrumentId, BigDecimal faceValue) {
        this.instrumentId = instrumentId;
        this.faceValue = faceValue;
        this.creationTime = LocalDateTime.now();
    }

    public abstract BigDecimal calculateCurrentValue(LocalDateTime asOfDate);
    public abstract CompletableFuture<Boolean> executeTransaction(BigDecimal amount, String counterparty);
    public abstract List<String> getRequiredDocuments();
    public abstract boolean validateInstrument();
    public abstract Map<String, Object> getRiskMetrics();

    public String getInstrumentId() { return instrumentId; }
    public BigDecimal getFaceValue() { return faceValue; }
    public LocalDateTime getCreationTime() { return creationTime; }
}

public class CorporateBond extends FinancialInstrument {
    private final BigDecimal couponRate;
    private final LocalDateTime maturityDate;
    private final String creditRating;
    private final List<LocalDateTime> couponPaymentDates;

    public CorporateBond(String instrumentId, BigDecimal faceValue, BigDecimal couponRate, 
                        LocalDateTime maturityDate, String creditRating) {
        super(instrumentId, faceValue);
        this.couponRate = couponRate;
        this.maturityDate = maturityDate;
        this.creditRating = creditRating;
        this.couponPaymentDates = generateCouponDates();
    }

    @Override
    public BigDecimal calculateCurrentValue(LocalDateTime asOfDate) {
        if (asOfDate.isAfter(maturityDate)) {
            return BigDecimal.ZERO;
        }
        BigDecimal presentValue = faceValue;
        for (LocalDateTime paymentDate : couponPaymentDates) {
            if (paymentDate.isAfter(asOfDate)) {
                BigDecimal couponPayment = faceValue.multiply(couponRate);
                presentValue = presentValue.add(couponPayment);
            }
        }
        return presentValue;
    }

    @Override
    public CompletableFuture<Boolean> executeTransaction(BigDecimal amount, String counterparty) {
        return CompletableFuture.supplyAsync(() -> {
            if (amount.compareTo(faceValue) != 0) {
                throw new IllegalArgumentException("Corporate bonds must be traded at face value");
            }
            if (!validateCounterparty(counterparty)) {
                return false;
            }
            logger.info("Executing bond transaction: " + instrumentId);
            return true;
        });
    }

    @Override
    public List<String> getRequiredDocuments() {
        return Arrays.asList("Bond Certificate", "Credit Rating Report", "Indenture Agreement");
    }

    @Override
    public boolean validateInstrument() {
        return creditRating != null && !creditRating.isEmpty() && 
               maturityDate.isAfter(LocalDateTime.now()) && 
               couponRate.compareTo(BigDecimal.ZERO) > 0;
    }

    @Override
    public Map<String, Object> getRiskMetrics() {
        Map<String, Object> metrics = new HashMap<>();
        metrics.put("creditRisk", creditRating);
        metrics.put("duration", calculateDuration());
        metrics.put("yieldToMaturity", calculateYTM());
        return metrics;
    }

    private List<LocalDateTime> generateCouponDates() {
        List<LocalDateTime> dates = new ArrayList<>();
        LocalDateTime current = creationTime.plusMonths(6);
        while (current.isBefore(maturityDate)) {
            dates.add(current);
            current = current.plusMonths(6);
        }
        return dates;
    }

    private boolean validateCounterparty(String counterparty) {
        return counterparty != null && counterparty.length() > 5;
    }

    private BigDecimal calculateDuration() {
        return BigDecimal.valueOf(2.5);
    }

    private BigDecimal calculateYTM() {
        return couponRate.multiply(BigDecimal.valueOf(1.1));
    }
}

public class DerivativeContract extends FinancialInstrument {
    private final String underlyingAsset;
    private final LocalDateTime expirationDate;
    private final BigDecimal strikePrice;
    private final String contractType;
    private volatile boolean isMarkedToMarket = false;

    public DerivativeContract(String instrumentId, BigDecimal notionalValue, String underlyingAsset,
                             LocalDateTime expirationDate, BigDecimal strikePrice, String contractType) {
        super(instrumentId, notionalValue);
        this.underlyingAsset = underlyingAsset;
        this.expirationDate = expirationDate;
        this.strikePrice = strikePrice;
        this.contractType = contractType;
    }

    @Override
    public BigDecimal calculateCurrentValue(LocalDateTime asOfDate) {
        if (!isMarkedToMarket) {
            throw new IllegalStateException("Derivative must be marked to market before valuation");
        }
        if (asOfDate.isAfter(expirationDate)) {
            return calculateExpirationValue();
        }
        return calculateTimeValue().add(calculateIntrinsicValue());
    }

    @Override
    public CompletableFuture<Boolean> executeTransaction(BigDecimal amount, String counterparty) {
        return CompletableFuture.supplyAsync(() -> {
            if (!isMarkedToMarket) {
                throw new IllegalStateException("Cannot trade unmarked derivative");
            }
            if (LocalDateTime.now().isAfter(expirationDate)) {
                throw new IllegalStateException("Cannot trade expired derivative");
            }
            if (amount.compareTo(BigDecimal.ZERO) <= 0) {
                throw new IllegalArgumentException("Transaction amount must be positive");
            }
            logger.info("Executing derivative transaction: " + instrumentId);
            return performComplexSettlement(amount, counterparty);
        });
    }

    @Override
    public List<String> getRequiredDocuments() {
        if ("SWAP".equals(contractType)) {
            return Arrays.asList("ISDA Master Agreement", "Confirmation", "Collateral Agreement", "Risk Disclosure");
        }
        return Arrays.asList("Derivative Confirmation", "Risk Disclosure", "Margin Agreement");
    }

    @Override
    public boolean validateInstrument() {
        boolean baseValidation = underlyingAsset != null && !underlyingAsset.isEmpty() &&
                               expirationDate.isAfter(LocalDateTime.now()) &&
                               strikePrice.compareTo(BigDecimal.ZERO) > 0;

        if ("SWAP".equals(contractType) && !isMarkedToMarket) {
            return false;
        }
        return baseValidation;
    }

    @Override
    public Map<String, Object> getRiskMetrics() {
        Map<String, Object> metrics = new HashMap<>();
        metrics.put("delta", calculateDelta());
        metrics.put("gamma", calculateGamma());
        metrics.put("vega", calculateVega());
        metrics.put("theta", calculateTheta());
        metrics.put("counterpartyRisk", "HIGH");
        if (!isMarkedToMarket) {
            metrics.put("valuationRisk", "EXTREME");
        }
        return metrics;
    }

    public void markToMarket() {
        this.isMarkedToMarket = true;
    }

    private BigDecimal calculateExpirationValue() {
        return strikePrice.subtract(faceValue).max(BigDecimal.ZERO);
    }

    private BigDecimal calculateTimeValue() {
        return BigDecimal.valueOf(100);
    }

    private BigDecimal calculateIntrinsicValue() {
        return BigDecimal.valueOf(50);
    }

    private boolean performComplexSettlement(BigDecimal amount, String counterparty) {
        try {
            Thread.sleep(3000);
            return true;
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return false;
        }
    }

    private BigDecimal calculateDelta() { return BigDecimal.valueOf(0.6); }
    private BigDecimal calculateGamma() { return BigDecimal.valueOf(0.05); }
    private BigDecimal calculateVega() { return BigDecimal.valueOf(0.3); }
    private BigDecimal calculateTheta() { return BigDecimal.valueOf(-0.02); }
}

from abc import ABC, abstractmethod
from decimal import Decimal
from datetime import datetime, timedelta
from typing import List, Dict, Any, Optional
import asyncio
import logging
from enum import Enum

class InstrumentType(Enum):
    BOND = "BOND"
    DERIVATIVE = "DERIVATIVE"
    EQUITY = "EQUITY"

class FinancialInstrument(ABC):
    def __init__(self, instrument_id: str, face_value: Decimal):
        self.instrument_id = instrument_id
        self.face_value = face_value
        self.creation_time = datetime.now()
        self.logger = logging.getLogger(self.__class__.__name__)

    @abstractmethod
    def calculate_current_value(self, as_of_date: datetime) -> Decimal:
        pass

    @abstractmethod
    async def execute_transaction(self, amount: Decimal, counterparty: str) -> bool:
        pass

    @abstractmethod
    def get_required_documents(self) -> List[str]:
        pass

    @abstractmethod
    def validate_instrument(self) -> bool:
        pass

    @abstractmethod
    def get_risk_metrics(self) -> Dict[str, Any]:
        pass

class CorporateBond(FinancialInstrument):
    def __init__(self, instrument_id: str, face_value: Decimal, coupon_rate: Decimal,
                 maturity_date: datetime, credit_rating: str):
        super().__init__(instrument_id, face_value)
        self.coupon_rate = coupon_rate
        self.maturity_date = maturity_date
        self.credit_rating = credit_rating
        self.coupon_payment_dates = self._generate_coupon_dates()

    def calculate_current_value(self, as_of_date: datetime) -> Decimal:
        if as_of_date > self.maturity_date:
            return Decimal('0')

        present_value = self.face_value
        for payment_date in self.coupon_payment_dates:
            if payment_date > as_of_date:
                coupon_payment = self.face_value * self.coupon_rate
                present_value += coupon_payment

        return present_value

    async def execute_transaction(self, amount: Decimal, counterparty: str) -> bool:
        if amount != self.face_value:
            raise ValueError("Corporate bonds must be traded at face value")

        if not self._validate_counterparty(counterparty):
            return False

        self.logger.info(f"Executing bond transaction: {self.instrument_id}")
        await asyncio.sleep(1)  # Simulate processing time
        return True

    def get_required_documents(self) -> List[str]:
        return ["Bond Certificate", "Credit Rating Report", "Indenture Agreement"]

    def validate_instrument(self) -> bool:
        return (self.credit_rating and 
                self.maturity_date > datetime.now() and 
                self.coupon_rate > Decimal('0'))

    def get_risk_metrics(self) -> Dict[str, Any]:
        return {
            "credit_risk": self.credit_rating,
            "duration": self._calculate_duration(),
            "yield_to_maturity": self._calculate_ytm()
        }

    def _generate_coupon_dates(self) -> List[datetime]:
        dates = []
        current = self.creation_time + timedelta(days=180)
        while current < self.maturity_date:
            dates.append(current)
            current += timedelta(days=180)
        return dates

    def _validate_counterparty(self, counterparty: str) -> bool:
        return counterparty and len(counterparty) > 5

    def _calculate_duration(self) -> Decimal:
        return Decimal('2.5')

    def _calculate_ytm(self) -> Decimal:
        return self.coupon_rate * Decimal('1.1')

class DerivativeContract(FinancialInstrument):
    def __init__(self, instrument_id: str, notional_value: Decimal, underlying_asset: str,
                 expiration_date: datetime, strike_price: Decimal, contract_type: str):
        super().__init__(instrument_id, notional_value)
        self.underlying_asset = underlying_asset
        self.expiration_date = expiration_date
        self.strike_price = strike_price
        self.contract_type = contract_type
        self._is_marked_to_market = False

    def calculate_current_value(self, as_of_date: datetime) -> Decimal:
        if not self._is_marked_to_market:
            raise RuntimeError("Derivative must be marked to market before valuation")

        if as_of_date > self.expiration_date:
            return self._calculate_expiration_value()

        return self._calculate_time_value() + self._calculate_intrinsic_value()

    async def execute_transaction(self, amount: Decimal, counterparty: str) -> bool:
        if not self._is_marked_to_market:
            raise RuntimeError("Cannot trade unmarked derivative")

        if datetime.now() > self.expiration_date:
            raise RuntimeError("Cannot trade expired derivative")

        if amount <= Decimal('0'):
            raise ValueError("Transaction amount must be positive")

        self.logger.info(f"Executing derivative transaction: {self.instrument_id}")
        return await self._perform_complex_settlement(amount, counterparty)

    def get_required_documents(self) -> List[str]:
        if self.contract_type == "SWAP":
            return ["ISDA Master Agreement", "Confirmation", "Collateral Agreement", "Risk Disclosure"]
        return ["Derivative Confirmation", "Risk Disclosure", "Margin Agreement"]

    def validate_instrument(self) -> bool:
        base_validation = (self.underlying_asset and 
                          self.expiration_date > datetime.now() and 
                          self.strike_price > Decimal('0'))

        if self.contract_type == "SWAP" and not self._is_marked_to_market:
            return False

        return base_validation

    def get_risk_metrics(self) -> Dict[str, Any]:
        metrics = {
            "delta": self._calculate_delta(),
            "gamma": self._calculate_gamma(),
            "vega": self._calculate_vega(),
            "theta": self._calculate_theta(),
            "counterparty_risk": "HIGH"
        }

        if not self._is_marked_to_market:
            metrics["valuation_risk"] = "EXTREME"

        return metrics

    def mark_to_market(self):
        self._is_marked_to_market = True

    def _calculate_expiration_value(self) -> Decimal:
        return max(self.strike_price - self.face_value, Decimal('0'))

    def _calculate_time_value(self) -> Decimal:
        return Decimal('100')

    def _calculate_intrinsic_value(self) -> Decimal:
        return Decimal('50')

    async def _perform_complex_settlement(self, amount: Decimal, counterparty: str) -> bool:
        await asyncio.sleep(3)  # Simulate complex settlement
        return True

    def _calculate_delta(self) -> Decimal:
        return Decimal('0.6')

    def _calculate_gamma(self) -> Decimal:
        return Decimal('0.05')

    def _calculate_vega(self) -> Decimal:
        return Decimal('0.3')

    def _calculate_theta(self) -> Decimal:
        return Decimal('-0.02')

import kotlinx.coroutines.*
import java.math.BigDecimal
import java.time.LocalDateTime
import java.util.concurrent.ConcurrentHashMap
import java.util.logging.Logger

enum class InstrumentType {
    BOND, DERIVATIVE, EQUITY
}

enum class RiskLevel {
    LOW, MEDIUM, HIGH, EXTREME
}

abstract class FinancialInstrument(
    protected val instrumentId: String,
    protected val faceValue: BigDecimal
) {
    protected val creationTime: LocalDateTime = LocalDateTime.now()
    protected val logger: Logger = Logger.getLogger(this::class.java.name)

    abstract suspend fun calculateCurrentValue(asOfDate: LocalDateTime): BigDecimal
    abstract suspend fun executeTransaction(amount: BigDecimal, counterparty: String): Boolean
    abstract fun getRequiredDocuments(): List<String>
    abstract fun validateInstrument(): Boolean
    abstract fun getRiskMetrics(): Map<String, Any>

    fun getInstrumentId(): String = instrumentId
    fun getFaceValue(): BigDecimal = faceValue
    fun getCreationTime(): LocalDateTime = creationTime
}

class CorporateBond(
    instrumentId: String,
    faceValue: BigDecimal,
    private val couponRate: BigDecimal,
    private val maturityDate: LocalDateTime,
    private val creditRating: String
) : FinancialInstrument(instrumentId, faceValue) {

    private val couponPaymentDates: List<LocalDateTime> = generateCouponDates()
    private val bondMetrics = ConcurrentHashMap<String, BigDecimal>()

    override suspend fun calculateCurrentValue(asOfDate: LocalDateTime): BigDecimal {
        if (asOfDate.isAfter(maturityDate)) {
            return BigDecimal.ZERO
        }

        var presentValue = faceValue
        couponPaymentDates.filter { it.isAfter(asOfDate) }
            .forEach { _ ->
                val couponPayment = faceValue.multiply(couponRate)
                presentValue = presentValue.add(couponPayment)
            }

        return presentValue
    }

    override suspend fun executeTransaction(amount: BigDecimal, counterparty: String): Boolean {
        if (amount.compareTo(faceValue) != 0) {
            throw IllegalArgumentException("Corporate bonds must be traded at face value")
        }

        if (!validateCounterparty(counterparty)) {
            return false
        }

        logger.info("Executing bond transaction: $instrumentId")
        delay(1000) // Simulate processing time
        return true
    }

    override fun getRequiredDocuments(): List<String> {
        return listOf("Bond Certificate", "Credit Rating Report", "Indenture Agreement")
    }

    override fun validateInstrument(): Boolean {
        return creditRating.isNotEmpty() && 
               maturityDate.isAfter(LocalDateTime.now()) && 
               couponRate > BigDecimal.ZERO
    }

    override fun getRiskMetrics(): Map<String, Any> {
        return mapOf(
            "creditRisk" to creditRating,
            "duration" to calculateDuration(),
            "yieldToMaturity" to calculateYTM(),
            "interestRateRisk" to RiskLevel.MEDIUM
        )
    }

    private fun generateCouponDates(): List<LocalDateTime> {
        val dates = mutableListOf<LocalDateTime>()
        var current = creationTime.plusMonths(6)

        while (current.isBefore(maturityDate)) {
            dates.add(current)
            current = current.plusMonths(6)
        }

        return dates
    }

    private fun validateCounterparty(counterparty: String): Boolean {
        return counterparty.isNotEmpty() && counterparty.length > 5
    }

    private fun calculateDuration(): BigDecimal {
        return bondMetrics.computeIfAbsent("duration") { BigDecimal("2.5") }
    }

    private fun calculateYTM(): BigDecimal {
        return bondMetrics.computeIfAbsent("ytm") { 
            couponRate.multiply(BigDecimal("1.1")) 
        }
    }
}

class DerivativeContract(
    instrumentId: String,
    notionalValue: BigDecimal,
    private val underlyingAsset: String,
    private val expirationDate: LocalDateTime,
    private val strikePrice: BigDecimal,
    private val contractType: String
) : FinancialInstrument(instrumentId, notionalValue) {

    @Volatile
    private var isMarkedToMarket = false
    private val greeks = ConcurrentHashMap<String, BigDecimal>()
    private val settlementCache = ConcurrentHashMap<String, Boolean>()

    override suspend fun calculateCurrentValue(asOfDate: LocalDateTime): BigDecimal {
        if (!isMarkedToMarket) {
            throw IllegalStateException("Derivative must be marked to market before valuation")
        }

        if (asOfDate.isAfter(expirationDate)) {
            return calculateExpirationValue()
        }

        return calculateTimeValue().add(calculateIntrinsicValue())
    }

    override suspend fun executeTransaction(amount: BigDecimal, counterparty: String): Boolean {
        if (!isMarkedToMarket) {
            throw IllegalStateException("Cannot trade unmarked derivative")
        }

        if (LocalDateTime.now().isAfter(expirationDate)) {
            throw IllegalStateException("Cannot trade expired derivative")
        }

        if (amount <= BigDecimal.ZERO) {
            throw IllegalArgumentException("Transaction amount must be positive")
        }

        logger.info("Executing derivative transaction: $instrumentId")
        return performComplexSettlement(amount, counterparty)
    }

    override fun getRequiredDocuments(): List<String> {
        return when (contractType) {
            "SWAP" -> listOf(
                "ISDA Master Agreement", 
                "Confirmation", 
                "Collateral Agreement", 
                "Risk Disclosure"
            )
            else -> listOf(
                "Derivative Confirmation", 
                "Risk Disclosure", 
                "Margin Agreement"
            )
        }
    }

    override fun validateInstrument(): Boolean {
        val baseValidation = underlyingAsset.isNotEmpty() &&
                           expirationDate.isAfter(LocalDateTime.now()) &&
                           strikePrice > BigDecimal.ZERO

        return if (contractType == "SWAP" && !isMarkedToMarket) {
            false
        } else {
            baseValidation
        }
    }

    override fun getRiskMetrics(): Map<String, Any> {
        val metrics = mutableMapOf<String, Any>(
            "delta" to calculateDelta(),
            "gamma" to calculateGamma(),
            "vega" to calculateVega(),
            "theta" to calculateTheta(),
            "counterpartyRisk" to RiskLevel.HIGH
        )

        if (!isMarkedToMarket) {
            metrics["valuationRisk"] = RiskLevel.EXTREME
        }

        return metrics
    }

    fun markToMarket() {
        isMarkedToMarket = true
        greeks.clear() // Recalculate greeks after marking to market
    }

    private fun calculateExpirationValue(): BigDecimal {
        return strikePrice.subtract(faceValue).max(BigDecimal.ZERO)
    }

    private fun calculateTimeValue(): BigDecimal {
        return BigDecimal("100")
    }

    private fun calculateIntrinsicValue(): BigDecimal {
        return BigDecimal("50")
    }

    private suspend fun performComplexSettlement(amount: BigDecimal, counterparty: String): Boolean {
        val cacheKey = "$counterparty-$amount"

        return settlementCache.computeIfAbsent(cacheKey) {
            runBlocking {
                delay(3000) // Simulate complex settlement process
                true
            }
        }
    }

    private fun calculateDelta(): BigDecimal {
        return greeks.computeIfAbsent("delta") { BigDecimal("0.6") }
    }

    private fun calculateGamma(): BigDecimal {
        return greeks.computeIfAbsent("gamma") { BigDecimal("0.05") }
    }

    private fun calculateVega(): BigDecimal {
        return greeks.computeIfAbsent("vega") { BigDecimal("0.3") }
    }

    private fun calculateTheta(): BigDecimal {
        return greeks.computeIfAbsent("theta") { BigDecimal("-0.02") }
    }
}

using System;
using System.Collections.Generic;
using System.Collections.Concurrent;
using System.Threading.Tasks;
using System.Numerics;
using Microsoft.Extensions.Logging;

public enum InstrumentType
{
    Bond, Derivative, Equity
}

public enum RiskLevel
{
    Low, Medium, High, Extreme
}

public abstract class FinancialInstrument
{
    protected readonly string InstrumentId;
    protected readonly decimal FaceValue;
    protected readonly DateTime CreationTime;
    protected readonly ILogger Logger;

    protected FinancialInstrument(string instrumentId, decimal faceValue, ILogger logger)
    {
        InstrumentId = instrumentId;
        FaceValue = faceValue;
        CreationTime = DateTime.Now;
        Logger = logger;
    }

    public abstract Task<decimal> CalculateCurrentValueAsync(DateTime asOfDate);
    public abstract Task<bool> ExecuteTransactionAsync(decimal amount, string counterparty);
    public abstract List<string> GetRequiredDocuments();
    public abstract bool ValidateInstrument();
    public abstract Dictionary<string, object> GetRiskMetrics();

    public string GetInstrumentId() => InstrumentId;
    public decimal GetFaceValue() => FaceValue;
    public DateTime GetCreationTime() => CreationTime;
}

public class CorporateBond : FinancialInstrument
{
    private readonly decimal _couponRate;
    private readonly DateTime _maturityDate;
    private readonly string _creditRating;
    private readonly List<DateTime> _couponPaymentDates;
    private readonly ConcurrentDictionary<string, decimal> _bondMetrics;

    public CorporateBond(string instrumentId, decimal faceValue, decimal couponRate, 
                        DateTime maturityDate, string creditRating, ILogger logger) 
        : base(instrumentId, faceValue, logger)
    {
        _couponRate = couponRate;
        _maturityDate = maturityDate;
        _creditRating = creditRating;
        _couponPaymentDates = GenerateCouponDates();
        _bondMetrics = new ConcurrentDictionary<string, decimal>();
    }

    public override async Task<decimal> CalculateCurrentValueAsync(DateTime asOfDate)
    {
        if (asOfDate > _maturityDate)
        {
            return 0m;
        }

        var presentValue = FaceValue;

        foreach (var paymentDate in _couponPaymentDates.Where(d => d > asOfDate))
        {
            var couponPayment = FaceValue * _couponRate;
            presentValue += couponPayment;
        }

        await Task.Delay(100); // Simulate calculation time
        return presentValue;
    }

    public override async Task<bool> ExecuteTransactionAsync(decimal amount, string counterparty)
    {
        if (amount != FaceValue)
        {
            throw new ArgumentException("Corporate bonds must be traded at face value");
        }

        if (!ValidateCounterparty(counterparty))
        {
            return false;
        }

        Logger.LogInformation($"Executing bond transaction: {InstrumentId}");
        await Task.Delay(1000);
        return true;
    }

    public override List<string> GetRequiredDocuments()
    {
        return new List<string>
        {
            "Bond Certificate",
            "Credit Rating Report",
            "Indenture Agreement"
        };
    }

    public override bool ValidateInstrument()
    {
        return !string.IsNullOrEmpty(_creditRating) &&
               _maturityDate > DateTime.Now &&
               _couponRate > 0;
    }

    public override Dictionary<string, object> GetRiskMetrics()
    {
        return new Dictionary<string, object>
        {
            ["creditRisk"] = _creditRating,
            ["duration"] = CalculateDuration(),
            ["yieldToMaturity"] = CalculateYTM(),
            ["interestRateRisk"] = RiskLevel.Medium
        };
    }

    private List<DateTime> GenerateCouponDates()
    {
        var dates = new List<DateTime>();
        var current = CreationTime.AddMonths(6);

        while (current < _maturityDate)
        {
            dates.Add(current);
            current = current.AddMonths(6);
        }

        return dates;
    }

    private bool ValidateCounterparty(string counterparty)
    {
        return !string.IsNullOrEmpty(counterparty) && counterparty.Length > 5;
    }

    private decimal CalculateDuration()
    {
        return _bondMetrics.GetOrAdd("duration", _ => 2.5m);
    }

    private decimal CalculateYTM()
    {
        return _bondMetrics.GetOrAdd("ytm", _ => _couponRate * 1.1m);
    }
}

public class DerivativeContract : FinancialInstrument
{
    private readonly string _underlyingAsset;
    private readonly DateTime _expirationDate;
    private readonly decimal _strikePrice;
    private readonly string _contractType;
    private volatile bool _isMarkedToMarket;
    private readonly ConcurrentDictionary<string, decimal> _greeks;
    private readonly ConcurrentDictionary<string, bool> _settlementCache;

    public DerivativeContract(string instrumentId, decimal notionalValue, string underlyingAsset,
                            DateTime expirationDate, decimal strikePrice, string contractType, ILogger logger)
        : base(instrumentId, notionalValue, logger)
    {
        _underlyingAsset = underlyingAsset;
        _expirationDate = expirationDate;
        _strikePrice = strikePrice;
        _contractType = contractType;
        _isMarkedToMarket = false;
        _greeks = new ConcurrentDictionary<string, decimal>();
        _settlementCache = new ConcurrentDictionary<string, bool>();
    }

    public override async Task<decimal> CalculateCurrentValueAsync(DateTime asOfDate)
    {
        if (!_isMarkedToMarket)
        {
            throw new InvalidOperationException("Derivative must be marked to market before valuation");
        }

        if (asOfDate > _expirationDate)
        {
            return CalculateExpirationValue();
        }

        var timeValue = await CalculateTimeValueAsync();
        var intrinsicValue = CalculateIntrinsicValue();

        return timeValue + intrinsicValue;
    }

    public override async Task<bool> ExecuteTransactionAsync(decimal amount, string counterparty)
    {
        if (!_isMarkedToMarket)
        {
            throw new InvalidOperationException("Cannot trade unmarked derivative");
        }

        if (DateTime.Now > _expirationDate)
        {
            throw new InvalidOperationException("Cannot trade expired derivative");
        }

        if (amount <= 0)
        {
            throw new ArgumentException("Transaction amount must be positive");
        }

        Logger.LogInformation($"Executing derivative transaction: {InstrumentId}");
        return await PerformComplexSettlementAsync(amount, counterparty);
    }

    public override List<string> GetRequiredDocuments()
    {
        return _contractType switch
        {
            "SWAP" => new List<string>
            {
                "ISDA Master Agreement",
                "Confirmation",
                "Collateral Agreement",
                "Risk Disclosure"
            },
            _ => new List<string>
            {
                "Derivative Confirmation",
                "Risk Disclosure",
                "Margin Agreement"
            }
        };
    }

    public override bool ValidateInstrument()
    {
        var baseValidation = !string.IsNullOrEmpty(_underlyingAsset) &&
                           _expirationDate > DateTime.Now &&
                           _strikePrice > 0;

        if (_contractType == "SWAP" && !_isMarkedToMarket)
        {
            return false;
        }

        return baseValidation;
    }

    public override Dictionary<string, object> GetRiskMetrics()
    {
        var metrics = new Dictionary<string, object>
        {
            ["delta"] = CalculateDelta(),
            ["gamma"] = CalculateGamma(),
            ["vega"] = CalculateVega(),
            ["theta"] = CalculateTheta(),
            ["counterpartyRisk"] = RiskLevel.High
        };

        if (!_isMarkedToMarket)
        {
            metrics["valuationRisk"] = RiskLevel.Extreme;
        }

        return metrics;
    }

    public void MarkToMarket()
    {
        _isMarkedToMarket = true;
        _greeks.Clear(); // Recalculate greeks after marking to market
    }

    private decimal CalculateExpirationValue()
    {
        return Math.Max(_strikePrice - FaceValue, 0);
    }

    private async Task<decimal> CalculateTimeValueAsync()
    {
        await Task.Delay(50);
        return 100m;
