    protected double fuelCapacity;
    protected double currentFuelLevel;

    public Vehicle(String brand, double fuelCapacity) {
        this.brand = brand;
        this.fuelCapacity = fuelCapacity;
        this.currentFuelLevel = 0;
    }

    public abstract void refuel(String fuelType, double amount);
    public abstract double getRemainingRange();

    public String getBrand() { return brand; }
    public double getFuelCapacity() { return fuelCapacity; }
    public double getCurrentFuelLevel() { return currentFuelLevel; }
}

public class GasCar extends Vehicle {
    private double milesPerGallon;

    public GasCar(String brand, double tankCapacity, double mpg) {
        super(brand, tankCapacity);
        this.milesPerGallon = mpg;
    }

    @Override
    public void refuel(String fuelType, double gallons) {
        if (!fuelType.equals("gasoline") && !fuelType.equals("premium") && !fuelType.equals("regular")) {
            throw new IllegalStateException("Gas vehicles cannot use " + fuelType + ". Only gasoline types allowed.");
        }
        if (gallons < 0) {
            throw new IllegalArgumentException("Cannot refuel negative amount");
        }
        double newLevel = Math.min(currentFuelLevel + gallons, fuelCapacity);
        this.currentFuelLevel = newLevel;
        System.out.println("Added " + gallons +" gallons of " + fuelType + " to " + brand);
    }

    @Override
    public double getRemainingRange() {
        return currentFuelLevel * milesPerGallon;
    }
}

public class ElectricCar extends Vehicle {
    private double batteryEfficiency;

    public ElectricCar(String brand, double batteryCapacity, double efficiency) {
        super(brand, batteryCapacity);
        this.batteryEfficiency = efficiency;
    }

    @Override
    public void refuel(String fuelType, double amount) {
        if (!fuelType.equals("electricity")) {
            throw new UnsupportedOperationException("Electric vehicles cannot be refueled with " + fuelType + ". Use charging station instead.");
        }
        if (amount < 0) {
            throw new IllegalArgumentException("Cannot charge negative amount");
        }
        double newLevel = Math.min(currentFuelLevel + amount, fuelCapacity);
        this.currentFuelLevel = newLevel;
        System.out.println("Charged " + amount + " kWh to " + brand + " battery");
    }

    @Override
    public double getRemainingRange() {
        return currentFuelLevel * batteryEfficiency;
    }

    public void chargeBattery(double kWh) {
        refuel("electricity", kWh);
    }
}

public class HybridCar extends Vehicle {
