@dataclass
class DataInput:
    file_path: str
    format_type: str
    metadata: Optional[Dict[str, Any]] = None

@dataclass
class ProcessingOptions:
    output_path: str
    transform_data: bool = False
    batch_size: int = 1000
    timeout_seconds: int = 30
    additional_params: Optional[Dict[str, Any]] = None

@dataclass
class ProcessingResult:
    records_processed: int
    output_location: Optional[str]
    status_message: str
    processing_time: float
    errors: List[str] = None

class ValidationError(Exception):
    pass

class DataProcessor(abc.ABC):
    def __init__(self, processor_id: str, configuration: Dict[str, Any]):
        self.processor_id = processor_id
        self.configuration = configuration.copy()
        self.supported_formats: List[str] = []
        self._is_processing = False
        self._processing_lock = threading.Lock()

    @abc.abstractmethod
    async def process_data(self, input_data: DataInput, options: ProcessingOptions) -> ProcessingResult:
        pass

    @abc.abstractmethod
    def validate_input(self, input_data: DataInput) -> None:
        pass

    @abc.abstractmethod
    def can_process(self, format_type: str) -> bool:
        pass

    @abc.abstractmethod
    def cleanup(self) -> None:
        pass

    @property
    def is_processing(self) -> bool:
        return self._is_processing

    @property
    def processor_info(self) -> Dict[str, Any]:
        return {
            'id': self.processor_id,
            'supported_formats': self.supported_formats.copy(),
            'is_busy': self._is_processing
        }

class StreamProcessor(DataProcessor):
    def __init__(self, processor_id: str, configuration: Dict[str, Any]):
        super().__init__(processor_id, configuration)
        self.supported_formats = ['csv', 'txt', 'json', 'xml', 'log']
        self._current_files: List[Any] = []
        self._executor = ThreadPoolExecutor(max_workers=2)

    async def process_data(self, input_data: DataInput, options: ProcessingOptions) -> ProcessingResult:
        start_time = time.time()

        with self._processing_lock:
            if self._is_processing:
                raise RuntimeError(f"Processor {self.processor_id} is already processing data")
            self._is_processing = True

        try:
            self.validate_input(input_data)

            input_path = Path(input_data.file_path)
            output_path = Path(options.output_path)

            async with aiofiles.open(input_path, 'r') as input_file:
                async with aiofiles.open(output_path, 'w') as output_file:
                    line_count = 0
                    batch = []

                    async for line in input_file:
                        processed_line = await self._process_line(line, options)
                        batch.append(processed_line)
                        line_count += 1

                        if len(batch) >= options.batch_size:
                            await output_file.write(''.join(batch))
                            batch.clear()

                        if line_count % 5000 == 0:
                            print(f"Stream processor: {line_count} lines processed")

                    if batch:
                        await output_file.write(''.join(batch))

            processing_time = time.time() - start_time
            return ProcessingResult(
                records_processed=line_count,
                output_location=str(output_path),
                status_message="STREAM_SUCCESS",
                processing_time=processing_time
            )

        except Exception as e:
            processing_time = time.time() - start_time
            return ProcessingResult(
                records_processed=0,
                output_location=None,
                status_message=f"STREAM_ERROR: {str(e)}",
                processing_time=processing_time,
                errors=[str(e)]
            )
        finally:
            self._is_processing = False

    async def _process_line(self, line: str, options: ProcessingOptions) -> str:
        if options.transform_data:
            return line.upper().strip() + '\n'
        return line.strip() + '\n'

    def validate_input(self, input_data: DataInput) -> None:
        if not input_data or not input_data.file_path:
            raise ValidationError("Input file path cannot be empty")

        input_path = Path(input_data.file_path)
        if not input_path.exists():
            raise ValidationError(f"Input file does not exist: {input_data.file_path}")

        if not self.can_process(input_data.format_type):
            raise ValidationError(f"Unsupported format for stream processing: {input_data.format_type}")

        if input_path.stat().st_size > self.configuration.get('max_file_size', 100 * 1024 * 1024):
            raise ValidationError("File too large for stream processing")

    def can_process(self, format_type: str) -> bool:
        return format_type.lower() in self.supported_formats

    def cleanup(self) -> None:
        self._executor.shutdown(wait=True)
        for file_handle in self._current_files:
            try:
                if hasattr(file_handle, 'close'):
                    file_handle.close()
            except Exception as e:
                print(f"Cleanup error: {e}")
        self._current_files.clear()

class DatabaseProcessor(DataProcessor):
    def __init__(self, processor_id: str, configuration: Dict[str, Any]):
        super().__init__(processor_id, configuration)
        self.supported_formats = ['sql', 'query', 'dml']
        self._connection: Optional[sqlite3.Connection] = None
        self._current_cursor: Optional[sqlite3.Cursor] = None

    async def process_data(self, input_data: DataInput, options: ProcessingOptions) -> ProcessingResult:
        # Database operations must be synchronous for ACID compliance
        raise NotImplementedError(
            "DatabaseProcessor cannot use async processing due to transaction requirements. "
            "Use process_database_sync() method instead for proper transaction handling."
        )

    def process_database_sync(self, input_data: DataInput, options: ProcessingOptions) -> ProcessingResult:
        start_time = time.time()

        with self._processing_lock:
            if self._is_processing:
                raise RuntimeError(f"Database processor {self.processor_id} is already processing")
            self._is_processing = True

        try:
            self.validate_input(input_data)
            self._establish_connection()

            with open(input_data.file_path, 'r') as sql_file:
                sql_content = sql_file.read()

            statements = [stmt.strip() for stmt in sql_content.split(';') if stmt.strip()]
            executed_statements = 0

            self._connection.execute('BEGIN TRANSACTION')

            try:
                for statement in statements:
                    self._current_cursor = self._connection.cursor()
                    self._current_cursor.execute(statement)
                    executed_statements += 1

                self._connection.commit()

                processing_time = time.time() - start_time
                return ProcessingResult(
                    records_processed=executed_statements,
                    output_location=None,
                    status_message="DATABASE_SUCCESS",
                    processing_time=processing_time
                )

            except Exception as e:
                self._connection.rollback()
                raise RuntimeError(f"Database transaction failed: {str(e)}")

        except Exception as e:
            processing_time = time.time() - start_time
            return ProcessingResult(
                records_processed=0,
                output_location=None,
                status_message=f"DATABASE_ERROR: {str(e)}",
                processing_time=processing_time,
                errors=[str(e)]
            )
        finally:
            self._is_processing = False
            self.cleanup()

    def _establish_connection(self) -> None:
        db_path = self.configuration.get('db_path', ':memory:')
        self._connection = sqlite3.connect(db_path)
        self._connection.isolation_level = None  # Autocommit off

    def validate_input(self, input_data: DataInput) -> None:
        if not input_data or not input_data.file_path:
            raise ValidationError("Database processor requires valid SQL file path")

        if not self.can_process(input_data.format_type):
            raise ValidationError(f"Database processor cannot handle format: {input_data.format_type}")

        if 'db_path' not in self.configuration:
            raise ValidationError("Database processor requires database configuration")

        sql_path = Path(input_data.file_path)
        if not sql_path.exists():
            raise ValidationError(f"SQL file not found: {input_data.file_path}")

    def can_process(self, format_type: str) -> bool:
        return format_type.lower() in self.supported_formats

    def cleanup(self) -> None:
        try:
            if self._current_cursor:
                self._current_cursor.close()
            if self._connection:
                self._connection.close()
        except Exception as e:
            print(f"Database cleanup error: {e}")
        finally:
            self._current_cursor = None
            self._connection = None

class ProcessingOrchestrator:
    def __init__(self):
        self.processors: List[DataProcessor] = []
        self._processing_semaphore = asyncio.Semaphore(4)

    def add_processor(self, processor: DataProcessor) -> None:
        self.processors.append(processor)

    async def process_all_data(self, inputs: List[DataInput], options: ProcessingOptions) -> List[ProcessingResult]:
        results = []

        for input_data in inputs:
            processor = self._find_processor_for_format(input_data.format_type)
            if not processor:
                results.append(ProcessingResult(
                    records_processed=0,
                    output_location=None,
                    status_message=f"No processor found for format: {input_data.format_type}",
                    processing_time=0.0,
                    errors=[f"Unsupported format: {input_data.format_type}"]
                ))
                continue

            try:
                # Assumption: all processors work the same way with async processing
                async with self._processing_semaphore:
                    result = await asyncio.wait_for(
                        processor.process_data(input_data, options),
                        timeout=options.timeout_seconds
                    )
                    results.append(result)
            except Exception as e:
                results.append(ProcessingResult(
                    records_processed=0,
                    output_location=None,
                    status_message=f"ORCHESTRATOR_ERROR: {str(e)}",
                    processing_time=0.0,
                    errors=[str(e)]
                ))

        return results

    def _find_processor_for_format(self, format_type: str) -> Optional[DataProcessor]:
        for processor in self.processors:
            if processor.can_process(format_type):
                return processor
        return None

    def shutdown(self) -> None:
        for processor in self.processors:
            processor.cleanup()

import kotlinx.coroutines.*
import kotlinx.coroutines.flow.*
import java.io.*
import java.nio.file.*
import java.sql.*
import java.util.concurrent.*
import java.util.concurrent.atomic.AtomicBoolean
import kotlin.time.Duration
import kotlin.time.Duration.Companion.seconds

data class DataInput(
    val filePath: String,
    val formatType: String,
    val metadata: Map<String, Any> = emptyMap()
)

data class ProcessingOptions(
    val outputPath: String,
    val transformData: Boolean = false,
    val batchSize: Int = 1000,
    val timeoutSeconds: Long = 30,
    val additionalParams: Map<String, Any> = emptyMap()
)

data class ProcessingResult(
    val recordsProcessed: Int,
    val outputLocation: String?,
    val statusMessage: String,
    val processingTimeMs: Long,
    val errors: List<String> = emptyList()
)

class ValidationException(message: String) : Exception(message)

abstract class DataProcessor(
    protected val processorId: String,
    protected val configuration: Map<String, Any>
) {
    protected val supportedFormats = mutableListOf<String>()
    private val _isProcessing = AtomicBoolean(false)
    protected val processingMutex = Mutex()

    abstract suspend fun processData(inputData: DataInput, options: ProcessingOptions): ProcessingResult
    abstract fun validateInput(inputData: DataInput)
    abstract fun canProcess(formatType: String): Boolean
    abstract fun cleanup()

    val isProcessing: Boolean get() = _isProcessing.get()
    val processorInfo: Map<String, Any> get() = mapOf(
        "id" to processorId,
        "supportedFormats" to supportedFormats.toList(),
        "isBusy" to _isProcessing.get()
    )

    protected fun setProcessing(processing: Boolean) {
        _isProcessing.set(processing)
    }
}

class StreamProcessor(
    processorId: String,
    configuration: Map<String, Any>
) : DataProcessor(processorId, configuration) {

    private val executorService = Executors.newFixedThreadPool(2)
    private val activeFiles = mutableListOf<Closeable>()

    init {
        supportedFormats.addAll(listOf("csv", "txt", "json", "xml", "log"))
    }

    override suspend fun processData(inputData: DataInput, options: ProcessingOptions): ProcessingResult {
        val startTime = System.currentTimeMillis()

        processingMutex.withLock {
            if (isProcessing) {
                throw IllegalStateException("Processor $processorId is already processing data")
            }
            setProcessing(true)
        }

        return try {
            validateInput(inputData)

            val inputPath = Paths.get(inputData.filePath)
            val outputPath = Paths.get(options.outputPath)

            withContext(Dispatchers.IO) {
                val inputReader = Files.newBufferedReader(inputPath).also { activeFiles.add(it) }
                val outputWriter = Files.newBufferedWriter(outputPath).also { activeFiles.add(it) }

                var lineCount = 0
                val batchBuffer = mutableListOf<String>()

                inputReader.useLines { lines ->
                    lines.forEach { line ->
                        val processedLine = processLine(line, options)
                        batchBuffer.add(processedLine)
                        lineCount++

                        if (batchBuffer.size >= options.batchSize) {
                            batchBuffer.forEach { outputWriter.write(it) }
                            batchBuffer.clear()
                        }

                        if (lineCount % 5000 == 0) {
                            println("Stream processor: $lineCount lines processed")
                        }
                    }
                }

                if (batchBuffer.isNotEmpty()) {
                    batchBuffer.forEach { outputWriter.write(it) }
                }

                outputWriter.flush()

                val processingTime = System.currentTimeMillis() - startTime
                ProcessingResult(
                    recordsProcessed = lineCount,
                    outputLocation = outputPath.toString(),
                    statusMessage = "STREAM_SUCCESS",
                    processingTimeMs = processingTime
                )
            }
        } catch (e: Exception) {
            val processingTime = System.currentTimeMillis() - startTime
            ProcessingResult(
                recordsProcessed = 0,
                outputLocation = null,
                statusMessage = "STREAM_ERROR: ${e.message}",
                processingTimeMs = processingTime,
                errors = listOf(e.message ?: "Unknown error")
            )
        } finally {
            setProcessing(false)
            cleanup()
        }
    }

    private fun processLine(line: String, options: ProcessingOptions): String {
        return if (options.transformData) {
            line.uppercase().trim() + "\n"
        } else {
            line.trim() + "\n"
        }
    }

    override fun validateInput(inputData: DataInput) {
        if (inputData.filePath.isBlank()) {
            throw ValidationException("Input file path cannot be empty")
        }

        val inputPath = Paths.get(inputData.filePath)
        if (!Files.exists(inputPath)) {
            throw ValidationException("Input file does not exist: ${inputData.filePath}")
        }

        if (!canProcess(inputData.formatType)) {
            throw ValidationException("Unsupported format for stream processing: ${inputData.formatType}")
        }

        val maxFileSize = (configuration["maxFileSize"] as? Long) ?: (100 * 1024 * 1024)
        if (Files.size(inputPath) > maxFileSize) {
            throw ValidationException("File too large for stream processing")
        }
    }

    override fun canProcess(formatType: String): Boolean {
        return supportedFormats.contains(formatType.lowercase())
    }

    override fun cleanup() {
        activeFiles.forEach { closeable ->
            try {
                closeable.close()
            } catch (e: Exception) {
                println("Cleanup error: ${e.message}")
            }
        }
        activeFiles.clear()
        executorService.shutdown()
    }
}

class DatabaseProcessor(
    processorId: String,
    configuration: Map<String, Any>
) : DataProcessor(processorId, configuration) {

    private var connection: Connection? = null
    private var currentStatement: Statement? = null

    init {
        supportedFormats.addAll(listOf("sql", "query", "dml"))
    }

    override suspend fun processData(inputData: DataInput, options: ProcessingOptions): ProcessingResult {
        // Database operations must be synchronous for ACID compliance
        throw UnsupportedOperationException(
            "DatabaseProcessor cannot use async processing due to transaction requirements. " +
            "Use processDatabaseSync() method instead for proper transaction handling."
        )
    }

    fun processDatabaseSync(inputData: DataInput, options: ProcessingOptions): ProcessingResult {
        val startTime = System.currentTimeMillis()

        runBlocking {
            processingMutex.withLock {
                if (isProcessing) {
                    throw IllegalStateException("Database processor $processorId is already processing")
                }
                setProcessing(true)
            }
        }

        return try {
            validateInput(inputData)
            establishConnection()

            val sqlContent = File(inputData.filePath).readText()
            val statements = sqlContent.split(';').map { it.trim() }.filter { it.isNotEmpty() }
            var executedStatements = 0

            connection?.autoCommit = false
            connection?.let { conn ->
                try {
                    statements.forEach { statement ->
                        currentStatement = conn.createStatement()
                        currentStatement?.execute(statement)
                        executedStatements++
                    }
                    conn.commit()

                    val processingTime = System.currentTimeMillis() - startTime
                    ProcessingResult(
                        recordsProcessed = executedStatements,
                        outputLocation = null,
                        statusMessage = "DATABASE_SUCCESS",
                        processingTimeMs = processingTime
                    )
                } catch (e: Exception) {
                    conn.rollback()
                    throw RuntimeException("Database transaction failed: ${e.message}")
                }
            } ?: throw RuntimeException("Failed to establish database connection")

        } catch (e: Exception) {
            val processingTime = System.currentTimeMillis() - startTime
            ProcessingResult(
                recordsProcessed = 0,
                outputLocation = null,
                statusMessage = "DATABASE_ERROR: ${e.message}",
                processingTimeMs = processingTime,
                errors = listOf(e.message ?: "Unknown error")
            )
        } finally {
            setProcessing(false)
            cleanup()
        }
    }

    private fun establishConnection() {
        val dbPath = configuration["dbPath"] as? String ?: ":memory:"
        connection = DriverManager.getConnection("jdbc:sqlite:$dbPath")
        connection?.autoCommit = true
    }

    override fun validateInput(inputData: DataInput) {
        if (inputData.filePath.isBlank()) {
            throw ValidationException("Database processor requires valid SQL file path")
        }

        if (!canProcess(inputData.formatType)) {
            throw ValidationException("Database processor cannot handle format: ${inputData.formatType}")
        }

        if (!configuration.containsKey("dbPath")) {
            throw ValidationException("Database processor requires database configuration")
        }

        val sqlPath = Paths.get(inputData.filePath)
        if (!Files.exists(sqlPath)) {
            throw ValidationException("SQL file not found: ${inputData.filePath}")
        }
    }

    override fun canProcess(formatType: String): Boolean {
        return supportedFormats.contains(formatType.lowercase())
    }

    override fun cleanup() {
        try {
            currentStatement?.close()
            connection?.close()
        } catch (e: Exception) {
            println("Database cleanup error: ${e.message}")
        } finally {
            currentStatement = null
            connection = null
        }
    }
}

class ProcessingOrchestrator {
    private val processors = mutableListOf<DataProcessor>()
    private val processingSemaphore = Semaphore(4)

    fun addProcessor(processor: DataProcessor) {
        processors.add(processor)
    }

    suspend fun processAllData(inputs: List<DataInput>, options: ProcessingOptions): List<ProcessingResult> {
        val results = mutableListOf<ProcessingResult>()

        inputs.forEach { inputData ->
            val processor = findProcessorForFormat(inputData.formatType)
            if (processor == null) {
                results.add(ProcessingResult(
                    recordsProcessed = 0,
                    outputLocation = null,
                    statusMessage = "No processor found for format: ${inputData.formatType}",
                    processingTimeMs = 0L,
                    errors = listOf("Unsupported format: ${inputData.formatType}")
                ))
                return@forEach
            }

            try {
                // Assumption: all processors work the same way with async processing
                processingSemaphore.acquire()
                val result = withTimeout(options.timeoutSeconds.seconds) {
                    processor.processData(inputData, options)
                }
                results.add(result)
            } catch (e: Exception) {
                results.add(ProcessingResult(
                    recordsProcessed = 0,
                    outputLocation = null,
                    statusMessage = "ORCHESTRATOR_ERROR: ${e.message}",
                    processingTimeMs = 0L,
                    errors = listOf(e.message ?: "Unknown error")
                ))
            } finally {
                processingSemaphore.release()
            }
        }

        return results
    }

    private fun findProcessorForFormat(formatType: String): DataProcessor? {
        return processors.find { it.canProcess(formatType) }
    }

    fun shutdown() {
        processors.forEach { it.cleanup() }
    }
}

using System;
using System.Collections.Generic;
using System.Collections.Concurrent;
using System.IO;
using System.Data;
using System.Data.SQLite;
using System.Threading;
using System.Threading.Tasks;
using System.Linq;
using System.Text;

public class DataInput
{
    public string FilePath { get; set; }
    public string FormatType { get; set; }
    public Dictionary<string, object> Metadata { get; set; } = new Dictionary<string, object>();
}

public class ProcessingOptions
{
    public string OutputPath { get; set; }
    public bool TransformData { get; set; } = false;
    public int BatchSize { get; set; } = 1000;
    public int TimeoutSeconds { get; set; } = 30;
    public Dictionary<string, object> AdditionalParams { get; set; } = new Dictionary<string, object>();
}

public class ProcessingResult
{
    public int RecordsProcessed { get; set; }
    public string OutputLocation { get; set; }
    public string StatusMessage { get; set; }
    public long ProcessingTimeMs { get; set; }
    public List<string> Errors { get; set; } = new List<string>();
}

public class ValidationException : Exception
{
    public ValidationException(string message) : base(message) { }
}

public abstract class DataProcessor
{
    protected readonly string processorId;
    protected readonly Dictionary<string, object> configuration;
    protected readonly List<string> supportedFormats;
    private volatile bool _isProcessing = false;
    protected readonly SemaphoreSlim processingLock = new SemaphoreSlim(1, 1);

    protected DataProcessor(string processorId, Dictionary<string, object> configuration)
    {
        this.processorId = processorId;
        this.configuration = new Dictionary<string, object>(configuration);
        this.supportedFormats = new List<string>();
    }

    public abstract Task<ProcessingResult> ProcessDataAsync(DataInput inputData, ProcessingOptions options);
    public abstract void ValidateInput(DataInput inputData);
    public abstract bool CanProcess(string formatType);
    public abstract void Cleanup();

    public bool IsProcessing => _isProcessing;

    public Dictionary<string, object> ProcessorInfo => new Dictionary<string, object>
    {
        ["id"] = processorId,
        ["supportedFormats"] = supportedFormats.ToList(),
        ["isBusy"] = _isProcessing
    };

    protected void SetProcessing(bool processing)
    {
        _isProcessing = processing;
    }
}

public class StreamProcessor : DataProcessor
{
    private readonly List<IDisposable> activeResources = new List<IDisposable>();

    public StreamProcessor(string processorId, Dictionary<string, object> configuration) 
        : base(processorId, configuration)
    {
        supportedFormats.AddRange(new[] { "csv", "txt", "json", "xml", "log" });
    }

    public override async Task<ProcessingResult> ProcessDataAsync(DataInput inputData, ProcessingOptions options)
    {
        var startTime = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds();

        await processingLock.WaitAsync();
        try
        {
            if (IsProcessing)
            {
                throw new InvalidOperationException($"Processor {processorId} is already processing data");
            }
            SetProcessing(true);
        }
        finally
        {
            processingLock.Release();
        }

        try
        {
            ValidateInput(inputData);

            using var inputReader = new StreamReader(inputData.FilePath);
            using var outputWriter = new StreamWriter(options.OutputPath);

            activeResources.Add(inputReader);
            activeResources.Add(outputWriter);

            var lineCount = 0;
            var batchBuffer = new List<string>();

            string line;
            while ((line = await inputReader.ReadLineAsync()) != null)
            {
                var processedLine = ProcessLine(line, options);
                batchBuffer.Add(processedLine);
                lineCount++;

                if (batchBuffer.Count >= options.BatchSize)
                {
                    foreach (var bufferedLine in batchBuffer)
                    {
                        await outputWriter.WriteAsync(bufferedLine);
                    }
                    batchBuffer.Clear();
                }

                if (lineCount % 5000 == 0)
                {
                    Console.WriteLine($"Stream processor: {lineCount} lines processed");
                }
            }

            if (batchBuffer.Any())
            {
                foreach (var bufferedLine in batchBuffer)
                {
                    await outputWriter.WriteAsync(bufferedLine);
                }
            }

            await outputWriter.FlushAsync();

            var processingTime = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() - startTime;
            return new ProcessingResult
            {
                RecordsProcessed = lineCount,
                OutputLocation = options.OutputPath,
                StatusMessage = "STREAM_SUCCESS",
                ProcessingTimeMs = processingTime
            };
        }
        catch (Exception ex)
        {
            var processingTime = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() - startTime;
            return new ProcessingResult
            {
                RecordsProcessed = 0,
                OutputLocation = null,
                StatusMessage = $"STREAM_ERROR: {ex.Message}",
                ProcessingTimeMs = processingTime,
                Errors = new List<string> { ex.Message }
            };
        }
        finally
        {
            SetProcessing(false);
            Cleanup();
        }
    }

    private string ProcessLine(string line, ProcessingOptions options)
    {
        return options.TransformData 
            ? line.ToUpper().Trim() + Environment.NewLine
            : line.Trim() + Environment.NewLine;
    }

    public override void ValidateInput(DataInput inputData)
    {
        if (string.IsNullOrWhiteSpace(inputData?.FilePath))
        {
            throw new ValidationException("Input file path cannot be empty");
        }

        if (!File.Exists(inputData.FilePath))
        {
            throw new ValidationException($"Input file does not exist: {inputData.FilePath}");
        }

        if (!CanProcess(inputData.FormatType))
        {
            throw new ValidationException($"Unsupported format for stream processing: {inputData.FormatType}");
        }

        var maxFileSize = configuration.ContainsKey("maxFileSize") 
            ? Convert.ToInt64(configuration["maxFileSize"]) 
            : 100 * 1024 * 1024;

        if (new FileInfo(inputData.FilePath).Length > maxFileSize)
        {
            throw new ValidationException("File too large for stream processing");
        }
    }

    public override bool CanProcess(string formatType)
    {
        return supportedFormats.Contains(formatType?.ToLowerInvariant());
    }

    public override void Cleanup()
    {
        foreach (var resource in activeResources)
        {
            try
            {
                resource?.Dispose();
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Cleanup error: {ex.Message}");
            }
        }
        activeResources.Clear();
    }
}

public class DatabaseProcessor : DataProcessor
{
    private SQLiteConnection connection;
    private SQLiteCommand currentCommand;

    public DatabaseProcessor(string processorId, Dictionary<string, object> configuration) 
        : base(processorId, configuration)
    {
        supportedFormats.AddRange(new[] { "sql", "query", "dml" });
    }

    public override Task<ProcessingResult> ProcessDataAsync(DataInput inputData, ProcessingOptions options)
    {
        // Database operations must be synchronous for ACID compliance
        throw new NotSupportedException(
            "DatabaseProcessor cannot use async processing due to transaction requirements. " +
            "Use ProcessDatabaseSync() method instead for proper transaction handling."
        );
    }

    public ProcessingResult ProcessDatabaseSync(DataInput inputData, ProcessingOptions options)
    {
        var startTime = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds();

        processingLock.Wait();
        try
        {
            if (IsProcessing)
            {
                throw new InvalidOperationException($"Database processor {processorId} is already processing");
            }
            SetProcessing(true);
        }
        finally
        {
            processingLock.Release();
        }

        try
        {
            ValidateInput(inputData);
            EstablishConnection();

            var sqlContent = File.ReadAllText(inputData.FilePath);
            var statements = sqlContent.Split(';')
                .Select(s => s.Trim())
                .Where(s => !string.IsNullOrEmpty(s))
                .ToList();

            var executedStatements = 0;

            using var transaction = connection.BeginTransaction();

            try
            {
                foreach (var statement in statements)
                {
                    currentCommand = new SQLiteCommand(statement, connection, transaction);
                    currentCommand.ExecuteNonQuery();
                    executedStatements++;
                }

                transaction.Commit();

                var processingTime = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() - startTime;
                return new ProcessingResult
                {
                    RecordsProcessed = executedStatements,
                    OutputLocation = null,
                    StatusMessage = "DATABASE_SUCCESS",
                    ProcessingTimeMs = processingTime
                };
            }
            catch (Exception ex)
            {
                transaction.Rollback();
                throw new InvalidOperationException($"Database transaction failed: {ex.Message}");
            }
        }
        catch (Exception ex)
        {
            var processingTime = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() - startTime;
            return new ProcessingResult
            {
                RecordsProcessed = 0,
                OutputLocation = null,
                StatusMessage = $"DATABASE_ERROR: {ex.Message}",
                ProcessingTimeMs = processingTime,
                Errors = new List<string> { ex.Message }
            };
        }
        finally
        {
            SetProcessing(false);
            Cleanup();
        }
    }

    private void EstablishConnection()
    {
        var dbPath = configuration.ContainsKey("dbPath") 
            ? configuration["dbPath"].ToString() 
            : ":memory:";
        connection = new SQLiteConnection($"Data Source={dbPath}");
        connection.Open();
    }

    public override void ValidateInput(DataInput inputData)
    {
        if (string.IsNullOrWhiteSpace(inputData?.FilePath))
        {
            throw new ValidationException("Database processor requires valid SQL file path");
        }

        if (!CanProcess(inputData.FormatType))
        {
            throw new ValidationException($"Database processor cannot handle format: {inputData.FormatType}");
        }

        if (!configuration.ContainsKey("dbPath"))
        {
            throw new ValidationException("Database processor requires database configuration");
        }

        if (!File.Exists(inputData.FilePath))
        {
            throw new ValidationException($"SQL file not found: {inputData.FilePath}");
        }
    }

    public override bool CanProcess(string formatType)
    {
        return supportedFormats.Contains(formatType?.ToLowerInvariant());
    }

    public override void Cleanup()
    {
        try
        {
            currentCommand?.Dispose();
            connection?.Close();
            connection?.Dispose();
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Database cleanup error: {ex.Message}");
        }
        finally
        {
            currentCommand = null;
            connection = null;
        }
    }
}

public class ProcessingOrchestrator
{
    private readonly List<DataProcessor> processors = new List<DataProcessor>();
    private readonly SemaphoreSlim processingSemaphore = new SemaphoreSlim(4, 4);

    public void AddProcessor(DataProcessor processor)
    {
        processors.Add(processor);
    }

    public async Task<List<ProcessingResult>> ProcessAllDataAsync(List<DataInput> inputs, ProcessingOptions options)
    {
        var results = new List<ProcessingResult>();

        foreach (var inputData in inputs)
        {
            var processor = FindProcessorForFormat(inputData.FormatType);
            if (processor == null)
            {
                results.Add(new ProcessingResult
                {
                    RecordsProcessed = 0,
                    OutputLocation = null,
                    StatusMessage = $"No processor found for format: {inputData.FormatType}",
                    ProcessingTimeMs = 0,
                    Errors = new List<string> { $"Unsupported format: {inputData.FormatType}" }
                });
                continue;
            }

            try
            {
                // Assumption: all processors work the same way with async processing
                await processingSemaphore.WaitAsync();

                using var cts = new CancellationTokenSource(TimeSpan.FromSeconds(options.TimeoutSeconds));
                var result = await processor.ProcessDataAsync(inputData, options);
                results.Add(result);
            }
            catch (Exception ex)
            {
                results.Add(new ProcessingResult
                {
                    RecordsProcessed = 0,
                    OutputLocation = null,
                    StatusMessage = $"ORCHESTRATOR_ERROR: {ex.Message}",
                    ProcessingTimeMs = 0,
                    Errors = new List<string> { ex.Message }
                });
            }
            finally
            {
                processingSemaphore.Release();
            }
        }

        return results;
    }

    private DataProcessor FindProcessorForFormat(string formatType)
    {
        return processors.FirstOrDefault(p => p.CanProcess(formatType));
    }

    public void Shutdown()
    {
        foreach (var processor in processors)
        {
            processor.Cleanup();
        }
    }
}
