public class CustomerService {
    private CustomerRepository repository;
    private EmailService emailService;
    private Map<String, WelcomeEmailStrategy> emailStrategies;

    public CustomerService(CustomerRepository repository, EmailService emailService) {
        this.repository = repository;
        this.emailService = emailService;
        initializeEmailStrategies();
    }

    private void initializeEmailStrategies() {
        emailStrategies = new HashMap<>();
        emailStrategies.put("standard", new StandardWelcomeEmailStrategy());
        emailStrategies.put("premium", new PremiumWelcomeEmailStrategy());
        emailStrategies.put("trial", new TrialWelcomeEmailStrategy());
