public class ReportExporter {
    private DataSource dataSource;
    private FileSystem fileSystem;
    private Map<String, ExportFormatStrategy> exportStrategies;

    public ReportExporter(DataSource dataSource, FileSystem fileSystem) {
        this.dataSource = dataSource;
        this.fileSystem = fileSystem;
        initializeExportStrategies();
    }

    private void initializeExportStrategies() {
        exportStrategies = new HashMap<>();
        exportStrategies.put("pdf", new PdfExportStrategy());
        exportStrategies.put("excel", new ExcelExportStrategy());
        exportStrategies.put("html", new HtmlExportStrategy());
        exportStrategies.put("csv", new CsvExportStrategy());
    }

    public void registerExportStrategy(String format, ExportFormatStrategy strategy) {
        exportStrategies.put(format, strategy);
    }

    public ExportResult exportReport(String reportId, String exportFormat, String destination) {
        Report report = dataSource.getReport(reportId);

        if (report == null) {
            return new ExportResult(false, "Report not found", null);
        }

        byte[] reportData = generateReportData(report, exportFormat);
        if (reportData == null) {
            return new ExportResult(false, "Failed to generate report", null);
        }

        String fileName = report.getTitle() + getFileExtension(exportFormat);
        fileSystem.writeFile(destination + "/" + fileName, reportData);
        return new ExportResult(true, "Report exported successfully", destination + "/" + fileName);
    }

    private byte[] generateReportData(Report report, String exportFormat) {
        try {
            ReportData data = dataSource.getReportData(report.getId());
            ExportFormatStrategy strategy = exportStrategies.get(exportFormat);

            if (strategy != null) {
                return strategy.generateReport(report, data);
            }
        } catch (Exception e) {
            return null;
        }
        return null;
    }

    private String getFileExtension(String format) {
        ExportFormatStrategy strategy = exportStrategies.get(format);
        return strategy != null ? strategy.getFileExtension() : ".txt";
    }
}

