public class TaxCalculator {
    public double calculateTax(Order order) {
        double totalTax = 0.0;

        if (order.getCountry().equals("US")) {
            Map<String, Double> stateTaxRates = getStateTaxRates();
            Map<String, Double> categoryTaxRates = getCategoryTaxRates();

            String state = order.getCustomerId().substring(0, 2);
            double stateTaxRate = stateTaxRates.getOrDefault(state, 0.05);

            for (OrderItem item : order.getItems()) {
                double itemPrice = item.getPrice() * item.getQuantity();
                double categoryRate = categoryTaxRates.getOrDefault(item.getCategory(), 0.0);
                double itemTax = itemPrice * (stateTaxRate + categoryRate);
                totalTax += itemTax;
            }
        } else if (order.getCountry().equals("EU")) {
            Map<String, Double> countryVatRates = getCountryVatRates();

            String countryCode = order.getCustomerId().substring(0, 2);
            double vatRate = countryVatRates.getOrDefault(countryCode, 0.20);

            for (OrderItem item : order.getItems()) {
                double itemPrice = item.getPrice() * item.getQuantity();
                totalTax += itemPrice * vatRate;
            }
        } else if (order.getCountry().equals("CA")) {
            Map<String, Double> provinceTaxRates = getProvinceTaxRates();
            double gst = 0.05;

            String province = order.getCustomerId().substring(0, 2);
            double provincialTax = provinceTaxRates.getOrDefault(province, 0.08);

            for (OrderItem item : order.getItems()) {
                double itemPrice = item.getPrice() * item.getQuantity();
                totalTax += itemPrice * (gst + provincialTax);
            }
        }

        return totalTax;
    }

    private Map<String, Double> getStateTaxRates() {
        Map<String, Double> rates = new HashMap<>();
        rates.put("CA", 0.0725);
        rates.put("NY", 0.045);
        rates.put("TX", 0.0625);
        return rates;
    }

    private Map<String, Double> getCategoryTaxRates() {
        Map<String, Double> rates = new HashMap<>();
        rates.put("luxury", 0.03);
        rates.put("food", 0.0);
        return rates;
    }

    private Map<String, Double> getCountryVatRates() {
        Map<String, Double> rates = new HashMap<>();
        rates.put("DE", 0.19);
        rates.put("FR", 0.20);
        rates.put("IT", 0.22);
        return rates;
    }

    private Map<String, Double> getProvinceTaxRates() {
        Map<String, Double> rates = new HashMap<>();
        rates.put("ON", 0.08);
        rates.put("BC", 0.07);
        rates.put("AB", 0.0);
        return rates;
    }
}

