        text = text.replaceAll("(?i)badword1|badword2|badword3", "[INAPPROPRIATE]");

        text = text.replaceAll("\d{3}-\d{2}-\d{4}", "[SSN]");
        text = text.replaceAll("\d{16}", "[CREDIT_CARD]");

        return text;
    }
}

public class ContentFilterChain {
    private List<ContentFilterStrategy> filters = new ArrayList<>();

    public void addFilter(ContentFilterStrategy filter) {
        filters.add(filter);
    }

    public String applyFilters(String text, Map<String, String> metadata) {
        String result = text;
        for (ContentFilterStrategy filter : filters) {
            result = filter.filter(result, metadata);
        }
        return result;
    }
}

public class ContentFilter {
    private Map<String, ContentFilterChain> filterChains = new HashMap<>();

    public ContentFilter() {
        ContentFilterChain commonChain = new ContentFilterChain();
        commonChain.addFilter(new CommonContentFilter());

        ContentFilterChain emailChain = new ContentFilterChain();
        emailChain.addFilter(new EmailContentFilter());
        emailChain.addFilter(new CommonContentFilter());

        ContentFilterChain socialChain = new ContentFilterChain();
        socialChain.addFilter(new SocialContentFilter());
        socialChain.addFilter(new CommonContentFilter());

        ContentFilterChain commentChain = new ContentFilterChain();
        commentChain.addFilter(new CommentContentFilter());
        commentChain.addFilter(new CommonContentFilter());

        filterChains.put("default", commonChain);
        filterChains.put("email", emailChain);
        filterChains.put("social", socialChain);
        filterChains.put("comment", commentChain);
    }

    public void registerFilterChain(String contentType, ContentFilterChain chain) {
        filterChains.put(contentType, chain);
    }

    public String filterContent(Content content) {
        String contentType = content.getType();
        ContentFilterChain chain = filterChains.getOrDefault(contentType, filterChains.get("default"));
        return chain.applyFilters(content.getText(), content.getMetadata());
    }
}

