import re
from abc import ABC, abstractmethod

class Content:
    def __init__(self, text, type, metadata):
        self.text = text
        self.type = type
        self.metadata = metadata

    def get_text(self):
        return self.text

    def get_type(self):
        return self.type

    def get_metadata(self):
        return self.metadata

class ContentFilterStrategy(ABC):
    @abstractmethod
    def filter(self, text, metadata):
        pass

class EmailContentFilter(ContentFilterStrategy):
    def filter(self, text, metadata):
        audience = metadata.get("audience", "general")

        if audience == "public":
            text = re.sub(r'(?i)confidential:', '[REDACTED]', text)
            text = re.sub(r'(?i)internal use only', '[INTERNAL]', text)

        text = re.sub(r'(?i)from:\s.*?\n', '', text)
        text = re.sub(r'(?i)to:\s.*?\n', '', text)
        text = re.sub(r'(?i)cc:\s.*?\n', '', text)

        return text

class SocialContentFilter(ContentFilterStrategy):
    def filter(self, text, metadata):
        platform = metadata.get("platform", "general")
        audience = metadata.get("audience", "general")

        if platform == "twitter":
            text = re.sub(r'@([A-Za-z0-9_]+)', '[USERNAME]', text)
        elif platform == "facebook":
            text = re.sub(r'@\[[^\]]+\]', '[NAME]', text)

        if audience == "minor":
            text = re.sub(r'(?i)alcohol|beer|wine', '[AGE-RESTRICTED]', text)

        return text

class CommentContentFilter(ContentFilterStrategy):
    def filter(self, text, metadata):
        context = metadata.get("context", "general")

        if context == "news":
            text = re.sub(r'(?i)allegedly|reportedly', '[STATEMENT]', text)
        elif context == "review":
            text = re.sub(r'(?i)terrible|awful|worst ever', 'not satisfactory', text)

        return text

class CommonContentFilter(ContentFilterStrategy):
    def filter(self, text, metadata):
        text = re.sub(r'(?i)badword1|badword2|badword3', '[INAPPROPRIATE]', text)
        text = re.sub(r'\d{3}-\d{2}-\d{4}', '[SSN]', text)
        text = re.sub(r'\d{16}', '[CREDIT_CARD]', text)
        return text

class ContentFilterChain:
    def __init__(self):
        self.filters = []

    def add_filter(self, filter):
        self.filters.append(filter)

    def apply_filters(self, text, metadata):
        result = text
        for filter in self.filters:
            result = filter.filter(result, metadata)
        return result

class ContentFilter:
    def __init__(self):
        self.filter_chains = {}

        common_chain = ContentFilterChain()
        common_chain.add_filter(CommonContentFilter())

        email_chain = ContentFilterChain()
        email_chain.add_filter(EmailContentFilter())
        email_chain.add_filter(CommonContentFilter())

        social_chain = ContentFilterChain()
        social_chain.add_filter(SocialContentFilter())
        social_chain.add_filter(CommonContentFilter())

        comment_chain = ContentFilterChain()
        comment_chain.add_filter(CommentContentFilter())
        comment_chain.add_filter(CommonContentFilter())

        self.filter_chains["default"] = common_chain
        self.filter_chains["email"] = email_chain
        self.filter_chains["social"] = social_chain
        self.filter_chains["comment"] = comment_chain

    def register_filter_chain(self, content_type, chain):
        self.filter_chains[content_type] = chain

    def filter_content(self, content):
        content_type = content.get_type()
        chain = self.filter_chains.get(content_type, self.filter_chains["default"])
        return chain.apply_filters(content.get_text(), content.get_metadata())

