class ContentFilterChain {
    private val filters = mutableListOf<ContentFilterStrategy>()

    fun addFilter(filter: ContentFilterStrategy) {
        filters.add(filter)
    }

    fun applyFilters(text: String, metadata: Map<String, String>): String {
        var result = text
        for (filter in filters) {
            result = filter.filter(result, metadata)
        }
        return result
    }
}

class ContentFilter {
    private val filterChains = HashMap<String, ContentFilterChain>()

    init {
        val commonChain = ContentFilterChain()
        commonChain.addFilter(CommonContentFilter())

        val emailChain = ContentFilterChain()
        emailChain.addFilter(EmailContentFilter())
        emailChain.addFilter(CommonContentFilter())

        val socialChain = ContentFilterChain()
        socialChain.addFilter(SocialContentFilter())
        socialChain.addFilter(CommonContentFilter())

        val commentChain = ContentFilterChain()
        commentChain.addFilter(CommentContentFilter())
        commentChain.addFilter(CommonContentFilter())

        filterChains["default"] = commonChain
        filterChains["email"] = emailChain
        filterChains["social"] = socialChain
        filterChains["comment"] = commentChain
    }

    fun registerFilterChain(contentType: String, chain: ContentFilterChain) {
        filterChains[contentType] = chain
    }

    fun filterContent(content: Content): String {
        val contentType = content.getType()
        val chain = filterChains[contentType] ?: filterChains["default"]!!
        return chain.applyFilters(content.getText(), content.getMetadata())
    }
}

