        emailStrategies["trial"] = new TrialWelcomeEmailStrategy();
    }

    public void RegisterEmailStrategy(string customerType, IWelcomeEmailStrategy strategy)
    {
        emailStrategies[customerType] = strategy;
    }

    public Customer RegisterCustomer(string name, string email, string customerType)
    {
        ValidateCustomerData(name, email);

        Customer customer = new Customer(name, email);
        repository.Save(customer);

        // Send welcome email based on customer type
        SendWelcomeEmail(customer, customerType);

        return customer;
    }

    private void ValidateCustomerData(string name, string email)
    {
        if (string.IsNullOrWhiteSpace(name))
        {
            throw new ArgumentException("Name cannot be empty");
        }

        if (string.IsNullOrEmpty(email) || !Regex.IsMatch(email, @"^[A-Za-z0-9+_.-]+@(.+)$"))
        {
            throw new ArgumentException("Invalid email format");
        }

        if (repository.FindByEmail(email) != null)
        {
            throw new ArgumentException("Email already registered");
        }
    }

    private void SendWelcomeEmail(Customer customer, string customerType)
    {
        if (!emailStrategies.TryGetValue(customerType, out IWelcomeEmailStrategy strategy))
        {
            throw new ArgumentException("Unsupported customer type: " + customerType);
        }

        string subject = "Welcome to Our Service";
        string body = strategy.CreateEmailBody(customer);

        emailService.SendEmail(customer.GetEmail(), subject, body);
    }
}

