public interface MultiFunctionDevice {
	void print(String document);
	void scan();
	void fax(String recipient);
}

public class BasicPrinter implements MultiFunctionDevice {
	@Override
	public void print(String document) {
		System.out.println("Printing: " + document);
	}

	@Override
	public void scan() {

		System.out.println("ERROR: This printer cannot scan documents!");
	}

	@Override
	public void fax(String recipient) {

		System.out.println("ERROR: This printer cannot send faxes!");
	}
}

public class AdvancedPrinter implements MultiFunctionDevice {
	@Override
	public void print(String document) {
		System.out.println("Printing: " + document);
	}

	@Override
	public void scan() {
		System.out.println("Scanning document...");
	}

	@Override
	public void fax(String recipient) {

		System.out.println("ERROR: This printer cannot send faxes!");
	}
}

public class SmartPhone implements MultiFunctionDevice {
	@Override
	public void print(String document) {
		System.out.println("Sending to cloud printer: " + document);
	}

	@Override
	public void scan() {
		System.out.println("Scanning document with camera...");
	}

	@Override
	public void fax(String recipient) {
		System.out.println("Sending digital fax to " + recipient);
	}
}

