    public void applyForJob(Resume resume) {
        throw new UnsupportedOperationException("Admins don't apply for jobs.");
    }

    @Override
    public void postJob(String jobDescription) {
        throw new UnsupportedOperationException("Admins don't post jobs.");
    }

    @Override
    public void reviewApplicants() {
        throw new UnsupportedOperationException("Admins don't review applicants.");
    }

    @Override
    public void manageSiteSettings() {
        System.out.println("Managing site settings...");
    }
}

