interface FlightBookingService {
    void addFlight(Flight flight);
    void updateFlightStatus(String flightNumber, FlightStatus status);
    List<Flight> searchAvailableFlights(String origin, String destination, LocalDateTime date);

    Booking createBooking(String passengerId, String flightNumber);
    void cancelBooking(String bookingId);
    Booking getBookingDetails(String bookingId);

    boolean processPayment(String bookingId, PaymentMethod method, double amount);
    boolean refundPayment(String bookingId);
}

interface NotificationService {
    void sendBookingConfirmation(String bookingId, String email);
    void sendCancellationNotice(String bookingId, String email);

    byte[] generateBookingReport(String bookingId);
    Map<String, Integer> getBookingStatistics(LocalDateTime start, LocalDateTime end);
}

class CustomerBookingSystem implements FlightBookingService {
    private Map<String, Flight> flights = new HashMap<>();
    private Map<String, Booking> bookings = new HashMap<>();

    public void setFlightsForDemo(Flight... flightsToAdd) {
        for (Flight flight : flightsToAdd) {
            flights.put(flight.getFlightNumber(), flight);
        }
    }

    @Override
    public void addFlight(Flight flight) {
        throw new UnsupportedOperationException("Customers cannot add flights");
    }

    @Override
    public void updateFlightStatus(String flightNumber, FlightStatus status) {
        throw new UnsupportedOperationException("Customers cannot update flight status");
    }

    @Override
    public List<Flight> searchAvailableFlights(String origin, String destination, LocalDateTime date) {
        List<Flight> availableFlights = new ArrayList<>();
        for (Flight flight : flights.values()) {
            if (flight.getOrigin().equals(origin) && 
                flight.getDestination().equals(destination) &&
                flight.getDepartureTime().toLocalDate().equals(date.toLocalDate())) {
                availableFlights.add(flight);
            }
        }
        return availableFlights;
    }

    @Override
    public Booking createBooking(String passengerId, String flightNumber) {
        Flight flight = flights.get(flightNumber);
        if (flight != null) {
            String bookingId = "B" + System.currentTimeMillis();
            Booking booking = new Booking(bookingId, passengerId, flightNumber);
            bookings.put(bookingId, booking);
            return booking;
        }
        return null;
    }

    @Override
    public void cancelBooking(String bookingId) {
        bookings.remove(bookingId);
    }

    @Override
    public Booking getBookingDetails(String bookingId) {
        return bookings.get(bookingId);
    }

    @Override
    public boolean processPayment(String bookingId, PaymentMethod method, double amount) {
        System.out.println("Processing payment of $" + amount + " for booking " + bookingId);
        return true;
    }

    @Override
    public boolean refundPayment(String bookingId) {
        System.out.println("Refunding payment for booking " + bookingId);
        return true;
    }
}

