from abc import ABC, abstractmethod

class MultiFunctionDevice(ABC):
    @abstractmethod
    def print(self, document):
        pass

    @abstractmethod
    def scan(self):
        pass

    @abstractmethod
    def fax(self, recipient):
        pass


class BasicPrinter(MultiFunctionDevice):
    def print(self, document):
        print(f"Printing: {document}")

    def scan(self):
        print("ERROR: This printer cannot scan documents!")

    def fax(self, recipient):
        print("ERROR: This printer cannot send faxes!")


class AdvancedPrinter(MultiFunctionDevice):
    def print(self, document):
        print(f"Printing: {document}")

    def scan(self):
        print("Scanning document...")

    def fax(self, recipient):
        print("ERROR: This printer cannot send faxes!")


class SmartPhone(MultiFunctionDevice):
    def print(self, document):
        print(f"Sending to cloud printer: {document}")

    def scan(self):
        print("Scanning document with camera...")

    def fax(self, recipient):
        print(f"Sending digital fax to {recipient}")

