from abc import ABC, abstractmethod

class Resume:
    def __init__(self, content):
        self.content = content

class PortalUser(ABC):
    @abstractmethod
    def search_jobs(self):
        pass

    @abstractmethod
    def apply_for_job(self, resume):
        pass

    @abstractmethod
    def post_job(self, job_description):
        pass

    @abstractmethod
    def review_applicants(self):
        pass

    @abstractmethod
    def manage_site_settings(self):
        pass

class JobSeeker(PortalUser):
    def search_jobs(self):
        print("Searching for jobs...")

    def apply_for_job(self, resume):
        print(f"Applied with resume: {resume.content}")

    def post_job(self, job_description):
        raise NotImplementedError("Job seekers can't post jobs.")

    def review_applicants(self):
        raise NotImplementedError("Job seekers can't review applicants.")

    def manage_site_settings(self):
        raise NotImplementedError("Job seekers can't manage site settings.")

class Recruiter(PortalUser):
    def search_jobs(self):
        raise NotImplementedError("Recruiters don't search for jobs.")

    def apply_for_job(self, resume):
        raise NotImplementedError("Recruiters don't apply for jobs.")

    def post_job(self, job_description):
