from abc import ABC, abstractmethod

class DeviceControl(ABC):
    @abstractmethod
    def turn_on(self):
        pass

    @abstractmethod
    def turn_off(self):
        pass

    @abstractmethod
    def volume_up(self):
        pass

    @abstractmethod
    def volume_down(self):
        pass

    @abstractmethod
    def lock(self):
        pass


class LightBulb:
    def __init__(self):
        self.is_on = False

    def turn_on(self):
        self.is_on = True
        print("LightBulb turned on.")

    def turn_off(self):
        self.is_on = False
        print("LightBulb turned off.")


class RegularLightBulb(LightBulb, DeviceControl):
    def volume_up(self):
        print("Volume up not supported on RegularLightBulb.")

    def volume_down(self):
        print("Volume down not supported on RegularLightBulb.")

    def lock(self):
        print("Lock not supported on RegularLightBulb.")


class RGBLightBulb(LightBulb, DeviceControl):
    def __init__(self):
        super().__init__()
        self.color = "White"

    def change_color(self, new_color):
        self.color = new_color
        print(f"RGBLightBulb color changed to {self.color}")

    def volume_up(self):
        print("Volume up not supported on RGBLightBulb.")

    def volume_down(self):
        print("Volume down not supported on RGBLightBulb.")

    def lock(self):
        print("Lock not supported on RGBLightBulb.")


class TV(DeviceControl):
    def __init__(self):
        self.is_on = False
        self.volume = 10

    def turn_on(self):
        self.is_on = True
        print("TV turned on.")

    def turn_off(self):
        self.is_on = False
        print("TV turned off.")

    def volume_up(self):
        self.volume += 1
        print(f"TV volume increased to {self.volume}")

    def volume_down(self):
        self.volume -= 1
        print(f"TV volume decreased to {self.volume}")

    def lock(self):
        print("TV locked (parental control enabled).")


class Speaker(DeviceControl):
    def __init__(self):
        self.is_on = False
        self.volume = 5

    def turn_on(self):
        self.is_on = True
        print("Speaker turned on.")

    def turn_off(self):
        self.is_on = False
        print("Speaker turned off.")

    def volume_up(self):
        self.volume += 1
        print(f"Speaker volume increased to {self.volume}")

    def volume_down(self):
        self.volume -= 1
        print(f"Speaker volume decreased to {self.volume}")

    def lock(self):
        print("Speaker locked.")


class SmartLock(DeviceControl):
    def __init__(self):
        self.is_locked = False

    def turn_on(self):
        print("SmartLock doesn't need to be turned on.")

    def turn_off(self):
        print("SmartLock doesn't need to be turned off.")

    def volume_up(self):
        print("Volume control not supported on SmartLock.")

    def volume_down(self):
        print("Volume control not supported on SmartLock.")

    def lock(self):
        self.is_locked = True
        print("SmartLock is now locked.")

