    def send_push_notification(self, device_id, title, message):
        raise NotImplementedError("SMSService does not support push notifications")

    def send_slack_message(self, channel, message):
        raise NotImplementedError("SMSService does not support Slack messages")


class EmailClient(NotificationReceiver):
    def receive_email(self, sender, subject, body):
