    def receive_sms(self, sender, message):
        raise NotImplementedError("EmailClient does not handle SMS")

    def receive_push_notification(self, app_id, title, message):
        raise NotImplementedError("EmailClient does not handle push notifications")

    def receive_slack_message(self, sender, message):
        raise NotImplementedError("EmailClient does not handle Slack messages")

