class CustomerBookingSystem : FlightBookingService {
    private val flights = mutableMapOf<String, Flight>()
    private val bookings = mutableMapOf<String, Booking>()

    fun setFlightsForDemo(vararg flightsToAdd: Flight) {
        for (flight in flightsToAdd) {
