        Console.WriteLine("Switch firmware updated to version: " + version);
    }

    public string GetDeviceName()
    {
        return name;
    }

    public void DisplayInfo()
    {
        Console.WriteLine("Network Switch: " + name + ", IP: " + ipAddress);
    }
}

