class DeviceManager
{
    public void ConfigureDevice(object device)
    {
        if (device is NetworkDevice networkDevice)
        {
            networkDevice.DisplayInfo();
            networkDevice.Connect();
        }
        else if (device is MultimediaDevice mediaDevice)
        {
            mediaDevice.DisplayInfo();
            mediaDevice.PowerOn();
        }
    }

    public void UpdateFirmware(NetworkDevice device)
    {
        Console.WriteLine("Updating firmware for " + device.GetDeviceName());
        device.UpdateFirmware("v2.4.1");
    }

    public void PlayMedia(MultimediaDevice device)
    {
        Console.WriteLine("Playing media on " + device.GetDeviceName());
        device.Play();
