from abc import ABC, abstractmethod
from datetime import datetime, timedelta
from typing import List, Dict


class FlightStatus:
    SCHEDULED = "SCHEDULED"
    BOARDING = "BOARDING"
    DEPARTED = "DEPARTED"
    LANDED = "LANDED"
    CANCELLED = "CANCELLED"
    DELAYED = "DELAYED"

