        self.booking_id = booking_id
        self.passenger_id = passenger_id
        self.flight_number = flight_number

class CustomerFlightSystem:
    def __init__(self):
        self.flights: Dict[str, Flight] = {}
        self.bookings: Dict[str, Booking] = {}

    def add_flight(self, flight: Flight) -> None:
        self.flights[flight.flight_number] = flight

    def search_available_flights(self, origin: str, destination: str, date: datetime) -> List[Flight]:
        return [f for f in self.flights.values() if f.departure_time.date() == date.date()]

    def create_booking(self, passenger_id: str, flight_number: str) -> Optional[Booking]:
        if flight_number in self.flights:
            booking_id = "B" + str(int(datetime.now().timestamp() * 1000))
            booking = Booking(booking_id, passenger_id, flight_number)
            self.bookings[booking_id] = booking
            return booking
        return None

    def cancel_booking(self, booking_id: str) -> None:
        if booking_id in self.bookings:
            del self.bookings[booking_id]

class AdminFlightSystem(CustomerFlightSystem):
    def update_flight_status(self, flight_number: str, status: str) -> None:
        if flight_number in self.flights:
