    {
        Console.WriteLine("Car stopped.");
    }

    public void drive()
    {
        Console.WriteLine("Car is driving on the road.");
    }

    public void fly()
    {
        throw new NotSupportedException("Car cannot fly.");
    }

    public void dive()
    {
        throw new NotSupportedException("Car cannot dive.");
    }
}

