    {
        Console.WriteLine("Submarine powered up.");
    }

    public void stop()
    {
        Console.WriteLine("Submarine powered down.");
    }

    public void drive()
    {
        throw new NotSupportedException("Submarine cannot drive on land.");
    }

    public void fly()
    {
        throw new NotSupportedException("Submarine cannot fly.");
    }

    public void dive()
    {
        Console.WriteLine("Submarine is diving.");
    }
}

