        Booking booking = new Booking(UUID.randomUUID().toString(), passengerId, flightNumber);
        bookings.put(booking.getBookingId(), booking);

        notificationService.sendNotification("Your booking has been confirmed", passengerId);

        return booking;
    }

    @Override
    public boolean cancelBooking(String bookingId) {
        Booking booking = bookings.remove(bookingId);
        if (booking == null) {
            throw new IllegalArgumentException("Invalid booking ID");
        }

        notificationService.sendNotification("Your booking has been cancelled", booking.getPassengerId());

        return true;
    }
}

