from abc import ABC, abstractmethod
import math


class Shape(ABC):
    @abstractmethod
    def calculate_area(self):
        pass

    @abstractmethod
    def calculate_perimeter(self):
        pass

    @abstractmethod
    def calculate_volume(self):
        pass

class Circle(Shape):
    def __init__(self, radius):
        self.radius = radius

    def calculate_area(self):
        return math.pi * self.radius ** 2

    def calculate_perimeter(self):
        return 2 * math.pi * self.radius

    def calculate_volume(self):
        print("Warning: Calculating volume for a 2D shape doesn't make sense!")
        return 0.0

class Rectangle(Shape):
    def __init__(self, width, height):
        self.width = width
        self.height = height

    def calculate_area(self):
        return self.width * self.height

    def calculate_perimeter(self):
        return 2 * (self.width + self.height)

    def calculate_volume(self):
        print("Warning: Calculating volume for a 2D shape doesn't make sense!")
        return 0.0

class Line(Shape):
    def __init__(self, length):
        self.length = length

    def calculate_area(self):
        print("Warning: Calculating area for a 1D shape doesn't make sense!")
        return 0.0

    def calculate_perimeter(self):
        return self.length

    def calculate_volume(self):
        print("Warning: Calculating volume for a 1D shape doesn't make sense!")
        return 0.0

class Cube(Shape):
    def __init__(self, side):
        self.side = side

    def calculate_area(self):
        return 6 * self.side ** 2

    def calculate_perimeter(self):
        return 12 * self.side

    def calculate_volume(self):
        return self.side ** 3

