    fun refundPayment(bookingId: String): Boolean
}

interface NotificationService {
    fun sendBookingConfirmation(bookingId: String, email: String)
    fun sendCancellationNotice(bookingId: String, email: String)

    fun generateBookingReport(bookingId: String): ByteArray
    fun getBookingStatistics(start: LocalDateTime, end: LocalDateTime): Map<String, Int>
}

class CustomerBookingSystem : FlightBookingService {
    private val flights = mutableMapOf<String, Flight>()
    private val bookings = mutableMapOf<String, Booking>()

    fun setFlightsForDemo(vararg flightsToAdd: Flight) {
        for (flight in flightsToAdd) {
            flights[flight.flightNumber] = flight
        }
    }

    override fun addFlight(flight: Flight) {
        throw UnsupportedOperationException("Customers cannot add flights")
    }

    override fun updateFlightStatus(flightNumber: String, status: FlightStatus) {
        throw UnsupportedOperationException("Customers cannot update flight status")
    }

    override fun searchAvailableFlights(origin: String, destination: String, date: LocalDateTime): List<Flight> {
        return flights.values.filter {
            it.origin == origin &&
            it.destination == destination &&
            it.departureTime.toLocalDate() == date.toLocalDate()
        }
    }

    override fun createBooking(passengerId: String, flightNumber: String): Booking? {
        val flight = flights[flightNumber]
        return if (flight != null) {
            val bookingId = "B" + System.currentTimeMillis()
            val booking = Booking(bookingId, passengerId, flightNumber)
            bookings[bookingId] = booking
            booking
        } else null
    }

    override fun cancelBooking(bookingId: String) {
        bookings.remove(bookingId)
    }

    override fun getBookingDetails(bookingId: String): Booking? {
        return bookings[bookingId]
    }

    override fun processPayment(bookingId: String, method: PaymentMethod, amount: Double): Boolean {
        println("Processing payment of $$amount for booking $bookingId")
        return true
    }

    override fun refundPayment(bookingId: String): Boolean {
        println("Refunding payment for booking $bookingId")
        return true
    }
}

class AdminFlightSystem : FlightBookingService {
    private val flights = mutableMapOf<String, Flight>()

    override fun addFlight(flight: Flight) {
        flights[flight.flightNumber] = flight
    }

    override fun updateFlightStatus(flightNumber: String, status: FlightStatus) {
        flights[flightNumber]?.status = status
    }

    override fun searchAvailableFlights(origin: String, destination: String, date: LocalDateTime): List<Flight> {
        return flights.values.filter {
