    def update_firmware(self, version: str):
        pass

    @abstractmethod
    def get_device_name(self):
        pass

    @abstractmethod
    def display_info(self):
        pass

class MultimediaDevice(ABC):
    @abstractmethod
    def power_on(self):
        pass

    @abstractmethod
    def play(self):
        pass

    @abstractmethod
    def stop(self):
        pass

    @abstractmethod
    def set_volume(self, level: int):
        pass

    @abstractmethod
    def set_screen_resolution(self, resolution: str):
        pass

    @abstractmethod
    def get_device_name(self):
        pass

    @abstractmethod
    def display_info(self):
        pass


class Router(NetworkDevice):
    def __init__(self, name, ip_address):
        self.name = name
        self.ip_address = ip_address

    def connect(self):
        print(f"Router {self.name} established connection.")

    def transfer_data(self, data):
        print(f"Router transferring data: {data}")

    def run_diagnostics(self):
        print(f"Running diagnostics on router {self.name}")

    def update_firmware(self, version):
