    def __init__(self, name, ip_address):
        self.name = name
        self.ip_address = ip_address

    def connect(self):
        print(f"Switch {self.name} established connection.")

    def transfer_data(self, data):
        print(f"Switch transferring data: {data}")

    def run_diagnostics(self):
        print(f"Running diagnostics on switch {self.name}")

    def update_firmware(self, version):
        print(f"Switch firmware updated to version: {version}")

    def get_device_name(self):
        return self.name

    def display_info(self):
        print(f"Network Switch: {self.name}, IP: {self.ip_address}")

class SmartTV(MultimediaDevice):
    def __init__(self, name, location):
        self.name = name
        self.location = location

    def power_on(self):
        print(f"{self.name} TV powered on.")

    def play(self):
        print(f"{self.name} TV playing content.")

    def stop(self):
        print(f"{self.name} TV stopped playback.")

    def set_volume(self, level):
        print(f"{self.name} TV volume set to {level}")

    def set_screen_resolution(self, resolution):
        print(f"{self.name} TV resolution set to {resolution}")

    def get_device_name(self):
        return self.name

    def display_info(self):
        print(f"Smart TV: {self.name}, Location: {self.location}")

