    private int[] paddings = { 0, 0, 0, 0 };

    public Panel(int x, int y, int width, int height)
    {
        X = x;
        Y = y;
        Width = width;
        Height = height;
    }

    public int X
    {
        get => x;
        set => x = value;
    }

    public int Y
    {
        get => y;
        set => y = value;
    }

    public int Width
    {
        get => width;
        set => width = value;
    }

    public int Height
    {
        get => height;
        set => height = value;
    }

    public string BackgroundColor
    {
        get => backgroundColor;
        set => SetBackground(value);
    }

    public string ForegroundColor
    {
        get => foregroundColor;
        set => SetForeground(value);
    }

    public int[] Margins
    {
        get => margins;
        set => SetMargin(value[0], value[1], value[2], value[3]);
    }

    public int[] Paddings
    {
        get => paddings;
        set => SetPadding(value[0], value[1], value[2], value[3]);
    }

    public void Render()
    {
        Console.WriteLine("Rendering panel");
        foreach (IUIComponent child in children)
        {
            child.Render();
        }
    }

    public void AddChild(IUIComponent child)
    {
        if (child == null) throw new ArgumentNullException(nameof(child));
        children.Add(child);
        Console.WriteLine("Child added to panel");
    }

    public void RemoveChild(IUIComponent child)
    {
        if (child == null) throw new ArgumentNullException(nameof(child));
        if (!children.Contains(child)) throw new ArgumentException("Child not found in the panel.");
        children.Remove(child);
        Console.WriteLine("Child removed from panel");
    }

    public IUIComponent GetParent()
    {
        return parent;
    }

    public void SetBackground(string color)
    {
        backgroundColor = color;
        Console.WriteLine($"Background color set to {color}");
    }

    public void SetForeground(string color)
    {
        foregroundColor = color;
        Console.WriteLine($"Foreground color set to {color}");
    }

    public void SetMargin(int top, int right, int bottom, int left)
    {
        margins = new[] { top, right, bottom, left };
        Console.WriteLine("Margins set");
    }

    public void SetPadding(int top, int right, int bottom, int left)
    {
        paddings = new[] { top, right, bottom, left };
        Console.WriteLine("Paddings set");
    }

    public void FadeIn(int durationMs)
    {
        Console.WriteLine($"Panel fading in over {durationMs} ms");
    }

    public void FadeOut(int durationMs)
    {
        Console.WriteLine($"Panel fading out over {durationMs} ms");
    }

    public void SlideIn(string direction, int durationMs)
    {
        Console.WriteLine($"Panel sliding in from {direction} over {durationMs} ms");
    }

    public void SlideOut(string direction, int durationMs)
    {
        Console.WriteLine($"Panel sliding out to {direction} over {durationMs} ms");
    }

    public void StopAnimations()
    {
        Console.WriteLine("Stopping all animations on panel");
    }
}

