{
    Booking CreateBooking(string passengerId, string flightNumber);
    bool CancelBooking(string bookingId);
}

// Notification Service Interface
public interface INotificationService
{
    void SendBookingConfirmation(string bookingId, string email);
    void SendCancellationNotice(string bookingId, string email);
}

// Reporting Service Interface
public interface IReportingService
{
    byte[] GenerateBookingReport(string bookingId);
    Dictionary<string, int> GetBookingStatistics(DateTime start, DateTime end);
}

// Customer Booking System (only needs booking management)
public class CustomerBookingSystem : IBookingManagementService
{
    public Booking CreateBooking(string passengerId, string flightNumber)
    {
        // Implementation for creating a booking
        return new Booking(Guid.NewGuid().ToString(), passengerId, flightNumber);
    }

    public bool CancelBooking(string bookingId)
    {
        // Implementation for canceling a booking
        Console.WriteLine("Booking " + bookingId + " has been canceled.");
        return true;
    }
}

// Admin Flight System (only needs flight management)
public class AdminFlightSystem : IFlightManagementService
{
    public void AddFlight(Flight flight)
    {
        // Implementation for adding a flight
        Console.WriteLine("Flight " + flight.GetFlightNumber() + " has been added.");
    }

    public void UpdateFlightStatus(string flightNumber, FlightStatus status)
    {
        // Implementation for updating flight status
        Console.WriteLine("Flight " + flightNumber + " status updated to " + status);
    }
}

// Email Service (only needs notification service)
public class EmailService : INotificationService
{
    public void SendBookingConfirmation(string bookingId, string email)
    {
        Console.WriteLine("Sending booking confirmation email for " + bookingId + " to " + email);
    }

    public void SendCancellationNotice(string bookingId, string email)
    {
        Console.WriteLine("Sending cancellation notice for " + bookingId + " to " + email);
    }
}

// Reporting Service (only needs reporting service)
public class ReportingService : IReportingService
{
    public byte[] GenerateBookingReport(string bookingId)
    {
        Console.WriteLine("Generating booking report for " + bookingId);
        return new byte[512]; // Return a dummy report with some size
    }

    public Dictionary<string, int> GetBookingStatistics(DateTime start, DateTime end)
    {
        Console.WriteLine("Generating booking statistics from " + start + " to " + end);
        Dictionary<string, int> stats = new Dictionary<string, int>();
        stats["totalBookings"] = 120;
        stats["cancelledBookings"] = 15;
        return stats;
    }
}
```

By splitting the interfaces into smaller, more specific ones, we ensure that each class only implements the methods it needs, adhering to the Interface Segregation Principle.

