    public void applyForJob(Resume resume) {
        System.out.println("Applied with resume: " + resume.content);
    }

    @Override
    public void postJob(String jobDescription) {
        throw new UnsupportedOperationException("Job seekers can't post jobs.");
    }

    @Override
    public void reviewApplicants() {
        throw new UnsupportedOperationException("Job seekers can't review applicants.");
    }

    @Override
    public void manageSiteSettings() {
        throw new UnsupportedOperationException("Job seekers can't manage site settings.");
    }
}

