    public void postJob(String jobDescription) {
        System.out.println("Job posted: " + jobDescription);
    }

    @Override
    public void reviewApplicants() {
        System.out.println("Reviewing applicants...");
    }

    @Override
    public void manageSiteSettings() {
        throw new UnsupportedOperationException("Recruiters can't manage site settings.");
    }
}

class Administrator implements PortalUser {
    @Override
    public void searchJobs() {
        throw new UnsupportedOperationException("Admins don't search for jobs.");
