interface Vehicle {
    void start();
    void stop();
    void drive();
    void fly();
    void dive();
}

class Car implements Vehicle {
    @Override
    public void start() {
        System.out.println("Car started.");
    }

    @Override
    public void stop() {
        System.out.println("Car stopped.");
    }

    @Override
    public void drive() {
        System.out.println("Car is driving on the road.");
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Car cannot fly.");
    }

    @Override
    public void dive() {
        throw new UnsupportedOperationException("Car cannot dive.");
    }
}

class Plane implements Vehicle {
    @Override
    public void start() {
        System.out.println("Plane engine started.");
    }

    @Override
    public void stop() {
        System.out.println("Plane engine stopped.");
    }

    @Override
    public void drive() {
        throw new UnsupportedOperationException("Plane cannot drive like a car.");
    }

    @Override
    public void fly() {
        System.out.println("Plane is flying.");
    }

    @Override
    public void dive() {
        throw new UnsupportedOperationException("Plane cannot dive underwater.");
    }
}

class Submarine implements Vehicle {
    @Override
    public void start() {
        System.out.println("Submarine powered up.");
    }

    @Override
    public void stop() {
        System.out.println("Submarine powered down.");
    }

    @Override
    public void drive() {
        throw new UnsupportedOperationException("Submarine cannot drive on land.");
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Submarine cannot fly.");
    }

    @Override
    public void dive() {
        System.out.println("Submarine is diving.");
    }
}

