```java
public interface LayoutManager {
    void layout(Panel panel);
}

public class FlowLayoutManager implements LayoutManager {
    @Override
    public void layout(Panel panel) {
        int x = panel.getPadding()[3]; // left padding
        int y = panel.getPadding()[0]; // top padding

        for (UIComponent child : panel.getChildren()) {
            child.setPosition(x, y);
            x += child.getWidth() + 5; // add some spacing

            if (x + child.getWidth() > panel.getWidth() - panel.getPadding()[1]) { // right padding
                x = panel.getPadding()[3];
                y += child.getHeight() + 5; // move to next line and add some spacing
            }
        }
    }
}

public class Panel extends UIComponent {
